INSERT INTO `questions` (`id`, `question_name`, `correct_answer`) VALUES
(7606, 'Câu 1 Hội nghị Ban Chấp hành Trung ương Đảng họp tháng 7-1936 chủ trương thành lập mặt trận nào?', 'B. Mặt trận nhân dân phản đế Đông Dương'),
(7607, 'Câu 2 Quốc tế Cộng sản họp Đại hội VII tại Mátxcơva (Liên Xô) (7-1935), xác định kẻ thù nguy hiểm trước mắt của nhân dân thế giới là?', 'D. Chủ nghĩa phát xít'),
(7608, 'Câu 3 Chỉ thị \"Nhật - Pháp bắn nhau và hành động của chúng ta\" đã xác định ai là kẻ thù chính của dân tộc?', 'A. Phát xít Nhật'),
(7609, 'Câu 4 Những khó khăn được ví như “ngàn cân treo sợi tóc” là nói về giai đoạn nào của cách mạng Việt Nam?', 'C. Giai đoạn 1945 – 1946.'),
(7610, 'Câu 5 Ai là người viết tác phẩm \"Tự chỉ trích\"?', 'A. Nguyễn Văn Cừ'),
(7611, 'Câu 6 Uỷ ban dân tộc giải phóng được thành lập do ai làm chủ tịch?', 'D. Hồ Chí Minh'),
(7612, 'Câu 7 Trong các điểm sau, chỉ rõ điểm khác nhau giữa Cương lĩnh chính trị đầu tiên của Đảng và Luận cương chính trị tháng 10-1930?', 'C. Chủ trương tập hợp lực lượng cách mạng.'),
(7613, 'Câu 8 Văn kiện nào của Đảng nhấn mạnh \"vấn đề thổ địa là cái cốt của cách mạng tư sản dân quyền\"?', 'A. Luận cương chính trị tháng 10-1930.'),
(7614, 'Câu 9 Thực dân pháp tiến hành khai thác thuộc địa lần thứ nhất ở việt nam khi nào?', 'C. 1897-1914'),
(7615, 'Câu 10 Tác phẩm Đường Cách mệnh đã thể hiện tư tưởng của lãnh tụ Nguyễn Ái Quốc dựa trên cơ sở nào?', 'D. Vận dụng sáng tạo chủ nghĩa Mác-Lênin vào đặc điểm của Việt Nam'),
(7616, 'Câu 11 Sơ thảo lần thứ nhất những Luận cương về vấn đề dân tộc và vấn đề thuộc địa của V.I.Lênin đã chỉ dẫn cho Nguyễn Ái Quốc vấn đề gì?', 'B. Hướng phát triển của sự nghiệp cứu nước, giải phóng dân tộc'),
(7617, 'Câu 12 Đại hội đại biểu lần thứ nhất của Đảng họp ở Ma Cao (3/1935) có ý nghĩa gì?', 'D. Sự phục hồi hệ thống tổ chức của Đảng và phong trào cách mạng quần chúng, tạo điều kiện để bước vào một cao trào cách mạng mới.'),
(7618, 'Câu 13 Đặc điểm ra đời của giai cấp công nhân Việt Nam là?', 'B. Đại bộ phận xuất thân từ nông dân.'),
(7619, 'Câu 14 Phong trào của tổ chức Việt Nam Quốc dân đảng do ai lãnh đạo?', 'B. Nguyễn Thái học'),
(7620, 'Câu 15 Đảng Cộng sản Việt Nam ra đời là sản phẩm của sự kết hợp giữa những nhân tố nào?', 'B. Chủ nghĩa Mác-Lênin, tư tưởng Hồ Chí Minh với phong trào công nhân và phong trào yêu nước Việt Nam.'),
(7621, 'Câu 16 Cương lĩnh đầu tiên của Đảng đã xác định phương hướng chiến lược của cách mạng Việt Nam là gì?', 'A. Làm tư sản dân quyền cách mạng và thổ địa cách mạng để đi tới xã hội cộng sản.'),
(7622, 'Câu 17 Sơ thảo lần thứ nhất những Luận cương về vấn đề dân tộc và vấn đề thuộc địa của V.I.Lênin đã chỉ dẫn cho Nguyễn Ái Quốc vấn đề gì?', 'C. Hướng phát triển của sự nghiệp cứu nước, giải phóng dân tộc'),
(7623, 'Câu 18 Sơ thảo lần thứ nhất những Luận cương về vấn đề dân tộc và vấn đề thuộc địa của V.I.Lênin đã chỉ dẫn cho Nguyễn Ái Quốc vấn đề gì?', 'A. Hướng phát triển của sự nghiệp cứu nước, giải phóng dân tộc'),
(7624, 'Câu 19 Nội dung nào sau đây nằm trong Cương lĩnh chính trị đầu tiên của Đảng?', 'B. Đánh đổ đế quốc chủ nghĩa Pháp và bọn phong kiến, làm cho nước Việt Nam hoàn toàn độc lập.'),
(7625, 'Câu 20 Luận điểm quan trọng của Nguyễn Ái Quốc về con đường cách mạng giải phóng các dân tộc bị áp bức là gì?', 'B. Giải phóng giai cấp, giải phóng dân tộc đó là sự nghiệp của chủ nghĩa cộng sản'),
(7626, 'Câu 21 Nguyễn Ái Quốc đọc bản Sơ thảo lần thứ nhất những luận cương về vấn đề dân tộc và vấn đề thuộc địa của V.I.Lênin vào thời gian nào?', 'C. Tháng 7/ 1920'),
(7627, 'Câu 22 Cương lĩnh chính trị đầu tiên của Đảng đã xác định đường lối chiến lược của cách mạng Việt Nam là gì?', 'A. Làm tư sản dân quyền cách mạng và thổ địa cách mạng để đi tới xã hội cộng sản.'),
(7628, 'Câu 23 Chủ trương chiến lược cách mạng mới của Đảng được đề ra bắt đầu từ Hội nghị Trung ương nào?', 'C. Hội nghị Trung ương 6 tháng 11/1939'),
(7629, 'Câu 24 Đảng Cộng sản Việt Nam ra đời là sản phẩm của sự kết hợp giữa những nhân tố nào?', 'D. Chủ nghĩa Mác-Lênin, tư tưởng Hồ Chí Minh với phong trào công nhân và phong trào yêu nước Việt Nam.'),
(7630, 'Câu 25 Cương lĩnh chính trị đầu tiên của Đảng đã xác định nhiệm vụ chủ yếu của trước mắt của cách mạng Việt Nam là gì?', 'A. Đánh đổ đế quốc chủ nghĩa Pháp và bọn phong kiến, làm cho nước Việt Nam hoàn toàn độc lập.'),
(7631, 'Câu 26 Trong cao trào kháng Nhật cứu nước, phong trào \"Phá kho thóc của Nhật để giải quyết nạn đói\" đã diễn ra mạnh mẽ ở đâu?', 'A. Đồng bằng Bắc Bộ và Bắc Trung Bộ'),
(7632, 'Câu 27 Xu hướng cải cách của Phan Châu Trinh với mục đích?', 'B. Khai dân trí, chấn dân khí, hậu dân sinh'),
(7633, 'Câu 28 Chính sách “chia để trị” của thực dân Pháp ở nước ta nhằm mục đích gì?', 'B. Nhằm phá vỡ khối đoàn kết cộng đồng quốc gia dân tộc'),
(7634, 'Câu 29 Một trong những chủ trương của xu hướng bạo động do Phan Bội Châu tổ chức, lãnh đạo với mục đích?', 'C. Tập hợp lực lượng với phương pháp bạo động chống Pháp'),
(7635, 'Câu 30 Văn kiện nào sau đây Đảng nêu quan điểm: “Cuộc dân tộc giải phóng không nhất thiết phải kết chặt với cuộc cách mạng điền địa Nghĩa là không thể nói rằng: muốn đánh đổ đế quốc cần phải phát triển cách mạng điền địa, muốn giải quyết vấn đề điền địa thì cần phải đánh đổ đế quốc Lý thuyết ấy có chỗ không xác đáng”?', 'B. Chung quanh vấn đề chiến sách mới (tháng 10-1936)'),
(7636, 'Câu 211 Cuộc kháng chiến chống thực dân Pháp kéo dài bao nhiêu năm?', 'A. 9 năm'),
(7637, 'Câu 212 Mỹ đã đưa Ngô Đình Diệm lên làm thủ tướng vào thời gian nào?', 'A. Ngày 7 - 7 - 1954'),
(7638, 'Câu 213 Mặt trận dân tộc giải phóng miền Nam Việt Nam ra đời khi nào?', 'A. Ngày 20 - 12 - 1960'),
(7639, 'Câu 214  Quân dân miền Bắc đập tan cuộc tập kích chiến lược bằng  của đế quốc Mỹ trong bao nhiêu ngày đêm và từ ngày nào đến này nào?', 'D. 12 ngày đêm từ 18 đến 30 tháng 12 năm 1972'),
(7640, 'Câu 215 Trung ương cục miền Nam được thành lập vào thời gian nào?', 'B. Tháng 10 - 1961'),
(7641, 'Câu 216 Hội nghị nào của Ban Chấp hành Trung ương Đảng(khoá II) đã thông qua Nghị quyết về Đường lối cách mạng miền Nam?', 'B. Hội nghị lần thứ 15'),
(7642, 'Câu 217 Nghị quyết về đường lối cách mạng miền Nam được thông qua tại Hội nghị TƯ, Đại hội nào?', 'C. Hội nghị TƯ 15 khoá II'),
(7643, 'Câu 218 Trong chiến tranh xâm lược Việt Nam (1945 - 1954) nước Pháp đã phải thay đổi bao nhiêu cao uỷ Pháp ở Đông Dương?', 'A. 7'),
(7644, 'Câu 219 Mỹ - Diệm đã ra luật 10 - 59 vào thời gian nào?', 'A. Ngày 6 - 5 - 1959'),
(7645, 'Câu 220 Đối với cách mạng Việt Nam, chiến thắng Điện Biên Phủ đã có ý nghĩa hết sức to lớn. Đó là:', 'B. Tất cả các phương án trên'),
(7646, 'Câu 221 Bản đề cương cách mạng miền Nam do ai chủ trì dự thảo?', 'A. Lê Duẩn'),
(7647, 'Câu 222 Đối với cách mạng thế giới, thắng lợi của quân và dân ta trong kháng chiến chống Pháp và can thiệp Mỹ, đặc biệt là chiến thắng Điện Biên Phủ đã:', 'D. Cả ba phương án trên'),
(7648, 'Câu 223 Ai được cử làm Bí thư Trung ương cục miền Nam đầu tiên?', 'D. Nguyễn Văn Linh'),
(7649, 'Câu 224 Ngày 8 - 5 - 1954, Hội nghị Giơnevơ bàn về chấm dứt cuộc chiến tranh ở Đông Dương khai mạc và kết thúc ngày:', 'A. Ngày 21 - 7 - 1954'),
(7650, 'Câu 225 Dự thảo “Đề cương cách mạng miền Nam” được viết vào thời gian nào?', 'C. Tháng 8 - 1956'),
(7651, 'Câu 226 Từ năm 1945 đến nay nhân dân ta đã tham gia bầu cử bao nhiêu khoá Quốc hội?', 'B. 11'),
(7652, 'Câu 227 Quân ta vào tiếp quản Thủ đô Hà Nội ngày nào?', 'B. Ngày 10 - 10 - 1954'),
(7653, 'Câu 228 Nêu một số nguyên nhân thắng lợi của cuộc kháng chiến chống thực dân Pháp của Việt Nam:', 'D. Cả 3 phương án trên'),
(7654, 'Câu 229 Hội nghị nào của Đảng mở đường cho phong trào \"Đồng khởi” ở miền Nam năm 1960?', 'D. Hội nghị Trung ương 15 - Khoá II của Đảng (1 - 1959)'),
(7655, 'Câu 230 Chủ tịch Hồ Chí Minh ra lời kêu gọi đồng bào và chiến sỹ cả nước: “Trung Nam Bắc đều là bờ cõi của ta, nước ta sẽ nhất định thống nhất, đồng bào cả nước nhất định được giải phóng” vào thời gian nào?', 'B. Ngày 22 - 7 - 1954'),
(7656, 'Câu 231 Hiệp định Pari về chấm dứt chiến tranh lập lại hoà bình ở Việt Nam được ký khi nào?', 'A. Ngày 27 - 1 - 1973'),
(7657, 'Câu 232 Trong chiến tranh xâm lược Việt Nam (1945 - 1954) quân đội Pháp ở Đông Dương đã mấy lần thay đổi Tổng chỉ huy?', 'D. 8'),
(7658, 'Câu 233 Hiệp định Giơnevơ về chấm dứt chiến tranh, lập lại hoà bình ở Đông Dương đã quy định:', 'C. Cả hai phương án a và b'),
(7659, 'Câu 234 Đường lối cách mạng xã hội chủ nghĩa ở miền Bắc được thông qua tại đại hội nào?', 'A. Đại hội III'),
(7660, 'Câu 235 Trận đọ sức quyết liệt đầu tiên giữa quân và dân ta với quân viễn chinh Mỹ vào thời gian nào?', 'D. Tháng 5 - 1965'),
(7661, 'Câu 236 Quốc hội khoá mấy đã quyết định đổi tên nước ta thành nước Cộng hoà xã hội chủ nghĩa Việt Nam?', 'A. Khoá 6'),
(7662, 'Câu 237 Kết thúc chiến dịch Điện Biên Phủ, quân và dân ta giành nhiều thắng lợi to lớn. Kết quả đã:', 'C. Cả hai phương án a và b'),
(7663, 'Câu 238 Ngay sau khi chiến dịch Điện Biên Phủ kết thúc, Hội nghị quốc tế về chấm dứt chiến tranh Đông Dương đã diễn ra tại:', 'A. Giơnevơ'),
(7664, 'Câu 239 Giải pháp ký kết hiệp định Giơnevơ, lập lại hoà bình ở Đông Dương (21 - 7 - 1954) đã thể hiện rằng:', 'C. Cả hai phương án b và c'),
(7665, 'Câu 240 Mỹ đã đưa quân viễn chinh Mỹ trực tiếp tham chiến ở Việt Nam khi nào?', 'B. 1965'),
(7666, 'Câu 241 Đại hội nào của Đảng khẳng định: Tư tưởng Hồ Chí Minh không chỉ là kết quả của sự vận dụng sáng tạo mà còn phát triển sáng tạo chủ nghĩa Mác - Lênin vào điều kiện cụ thể của nước ta?', 'A. Đại hội IX'),
(7667, 'Câu 242 Trong chiến tranh ở Việt Nam, Mỹ đã sử dụng mấy chiến lược chiến tranh?', 'B. 4 chiến lược'),
(7668, 'Câu 243 Chủ trương đổi mới công tác kế hoạch hoá và cải tiến một cách cơ bản chính sách kinh tế để làm cho sản xuất “bung ra” được nêu lên ở Hội nghị nào của Trung ương Đảng, khoá IV?', 'D. Hội nghị lần thứ sáu (8 - 1979)'),
(7669, 'Câu 244 Hội nghị nào của BCH Trung ương Đảng khoá V quyết định phải dứt khoát xoá bỏ cơ chế tập trung quan liêu bao cấp, thực hiện hạch toán kinh doanh xã hội chủ nghĩa?', 'D. Hội nghị lần thứ tám (6 - 1985)'),
(7670, 'Câu 245 Hội nghị nào của Đảng đã quyết định mở cuộc Tổng tấn công và nổi dậy Mậu Thân 1968?', 'C. Hội nghị Bộ Chính trị (12 - 1967)'),
(7671, 'Câu 246 Ngày 14 - 7 - 1986, tại Hội nghị Ban chấp hành TW đặc biệt ai được bầu làm Tổng Bí thư?', 'B. Trường Chinh'),
(7672, 'Câu 247 Bộ Chính trị đã ra chỉ thị về việc lãnh đạo tổng tuyển cử trong cả nước vào ngày nào?', 'C. Ngày 3 - 1 - 1976'),
(7673, 'Câu 248 Đại hội IV của Đảng đã rút ra bao nhiêu bài học kinh nghiệm?', 'D. 4'),
(7674, 'Câu 249 Chủ trương xây dựng và tổ chức thực hiện ba chương trình kinh tế lớn về lương thực, thực phẩm; hàng tiêu dùng; hàng xuất khẩu được Đảng đề ra tại Đại hội nào?', 'A. Đại hội lần thứ VI'),
(7675, 'Câu 250 Chiến lược Chiến tranh đơn phương của đế quốc Mỹ ở miền Nam diễn ra trong giai đoạn nào?', 'D. 1954 - 1960'),
(7676, 'Câu 251 Đại hội nào của Đảng đã chủ trương coi nông nghiệp là mặt trận hàng đầu?', 'A. Đại hội V'),
(7677, 'Câu 252 Hội nghị hiệp thương chính trị giữa đoàn đại biểu miền Bắc và đoàn đại biểu miền Nam đã họp ở đâu?', 'A. Sài Gòn'),
(7678, 'Câu 253 Thời kỳ Mỹ thực hiện chiến lược “Chiến tranh đặc biệt” của Kennơđi (1961 - 1964), chính quyền Sài Gòn bị Mỹ thay đổi mấy lần?', 'A. 10'),
(7679, 'Câu 254 Hội nghị nào của Đảng quyết định mở cuộc Tổng tấn công và nổi dậy giải phòng Sài Gòn trước tháng 5 - 1975?', 'B. Hội nghị Bộ Chính trị (3 - 1975)'),
(7680, 'Câu 255 Hội nghị nào của BCH Trung ương Đảng đề ra chủ trương hoàn thành thống nhất nước nhà về mặt nhà nước, đưa cả nước tiến lên chủ nghĩa xã hội?', 'D. Hội nghị TƯ 24 Khoá III. 9 - 1975'),
(7681, 'Câu 256 Kỳ họp thứ nhất của Quốc hội nước Việt Nam thống nhất (Quốc hội khoá VI) được tổ chức trong thời gian nào?', 'C. Từ 24 - 6 đến 3 - 7 - 1976'),
(7682, 'Câu 257 Câu nói: “Miền Nam là máu của máu Việt Nam, là thịt của thịt Việt Nam. Sông có thể cạn, núi có thể mòn song chân lý đó không bao giờ thay đổi” là của ai?', 'C. Hồ Chí Minh'),
(7683, 'Câu 258 Kế hoạch 5 năm lần thứ II (1976 - 1980) do đại hội IV thông qua đã đề ra bao nhiêu mục tiêu được xem là cơ bản, vừa là cấp bách?', 'D. 2 mục tiêu'),
(7684, 'Câu 259 “Nước ta đã ra khỏi khủng hoảng kinh tế - xã hội, nhưng một số mặt còn chưa vững chắc” là đánh giá tổng quát của Đại hội nào?', 'C. Đại hội VIII'),
(7685, 'Câu 260 Đế quốc Mỹ phải chấp nhận cuộc đàm phán với Chính phủ nước Việt Nam dân chủ cộng hoà ở Pari vào thời gian nào?', 'B. Tháng 1 - 1969'),
(7686, 'Câu 261 Trong các nguồn lực để công nghiệp hóa, hiện đại hóa ở nước ta đến năm 2020, Đại hội VIII của Đảng đã xác định nguồn lực nào là yếu tố cơ bản cho sự phát triển nhanh và bền vững?', 'C. Con người'),
(7687, 'Câu 262 Chỉ thị 100 CT - TW của ban Bí thư Trung ương Đảng về khoán sản phẩm đến nhóm và người lao động trong hợp tác xã được ban hành năm nào?', 'B. 1981'),
(7688, 'Câu 263 Câu nói: “Tất cả chúng ta hãy đoàn kết nhất trí triệu người như một, quyết tâm đánh thắng giặc Mỹ xâm lược...” là của ai?', 'C. Hồ Chí Minh'),
(7689, 'Câu 264 Đại hội nào của Đảng được gọi là Đại hội “Trí tuệ, đổi mới, dân chủ, kỷ cương và đoàn kết”?', 'B. Đại hội lần thứ VII'),
(7690, 'Câu 265 Câu nói: \"Chiến tranh có thể kéo dài năm năm, 10 năm, 20 năm hoặc lâu hơn nữa. Hà Nội, Hải phòng và một số thành phố, xí nghiệp có thể bị tàn phá, song nhân dân Việt Nam quyết không sợ! Không có gì quý hơn độc lập, tự do. đến ngày thắng lợi nhân dân ta sẽ xây dựng lại đất nước ta đàng hoàng hơn, to đẹp hơn!” của Chủ tịch Hồ Chí Minh viết thời gian nào, trong tác phẩm nào?.', 'D. Lời kêu gọi Ngày 17 - 7 - 1966'),
(7691, 'Câu 266 Chỉ thị 100 CT - TW của ban Bí thư Trung ương Đảng (1 - 1981) đưa ra chủ trương nào sau đây:', 'D. Khoán sản phẩm đến nhóm và người lao động trong hợp tác xã nông nghiệp.'),
(7692, 'Câu 267 Có bao nhiêu người Mỹ đã tự thiêu để phản đối chiến tranh của Mỹ ở Việt Nam?', 'C. 8'),
(7693, 'Câu 268 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội được thông qua trong Đại hội nào của Đảng?', 'A. Đại hội VII'),
(7694, 'Câu 269 Sự nghiệp công nghiệp hoá, hiện đại hoá đất nước được Đảng ta xác định sẽ cơ bản hoàn thành vào năm nào?', 'C. 2020'),
(7695, 'Câu 270 Từ khi ra đời đến nay Đảng ta có bao nhiêu cương lĩnh?', 'A. 4'),
(7696, 'Câu 271 Tại Đại hội nào của Đảng CSVN coi: “Giáo dục là quốc sách hàng đầu”?', 'A. Đại hội lần thứ VII'),
(7697, 'Câu 272 Chiến lược “Chiến tranh đặc biệt” của Mỹ tiến hành ở miền Nam Việt Nam gồm mấy bước?', 'A. 3 bước'),
(7698, 'Câu 273 Hội nghị nào của Ban Chấp hành Trung ương Đảng khoá V đã quyết định phải dứt khoát xoá bỏ cơ chế tập trung quan liêu bao cấp, thực hiện hạch toán kinh doanh xã hội chủ nghĩa?', 'A. Hội nghị lần thứ tám (6 - 1985)'),
(7699, 'Câu 274 Trong cuộc chiến tranh Việt Nam, chính phủ Mỹ đã phải thay bao nhiêu Tổng tư lệnh quân viễn chinh Mỹ ở Việt Nam?', 'D. 4'),
(7700, 'Câu 275 Mỹ tiến hành cuộc đảo chính ở Campuchia vào thời gian nào?', 'D. Tháng 3 - 1970'),
(7701, 'Câu 276 Cuộc hành quân “Lam Sơn 719” của Mỹ - Nguỵ bị đánh bại vào thời gian nào?', 'D. 1971'),
(7702, 'Câu 277 Chiến thắng Vạn Tường (Quảng Ngãi) vào thời gian nào?', 'A. Tháng 8 - 1965'),
(7703, 'Câu 1 Chi bộ Cộng sản đầu tiên ở Việt Nam được thành lập vào thời gian nào?', 'C. Tháng 3 năm 1929'),
(7704, 'Câu 2 Quốc tế Cộng sản xác định: “Kẻ thù nguy hiểm trước mắt của giai cấp vô sản và nhân dân lao động thế giới lúc này chưa phải là chủ nghĩa đế quốc nói chung mà là chủ nghĩa phát xít” tại Đại hội nào sau đây?', 'C. Đại hội lần thứ VII Quốc tế Cộng sản (1935).'),
(7705, 'Câu 3 Đảng xác định: “Kẻ thù trước mắt nguy hại nhất của nhân dân Đông Dương cần tập trung đánh đổ là bọn phản động thuộc địa và bè lũ tay sai của chúng” vào thời kỳ nào sau đây?', 'D. Thời kỳ 1936-1939.'),
(7706, 'Câu 4 Vấn đề: ‘Thổ địa cách mạng\' trong Cương lĩnh chính trị đầu tiên của Đàng được hiểu là gì?', 'D. Vấn đề ruộng đất cho dân cày'),
(7707, 'Câu 5 Quốc tế Cộng sản vạch ra nhiệm vụ trước mắt của giai cấp công nhân và nhân dân lao động thế giới lúc này chưa phải là lật đổ chủ nghĩa tư bản, giành chính quyền mà là \"đấu tranh chống chủ nghĩa phát xít, chống chiến tranh, bảo vệ dân chủ và hoà bình” tại Đại hội nào sau đây?', 'A. Đại hội lần thứ VII Quốc tế Cộng sản (1935).'),
(7708, 'Câu 6 Trong các nội dung dưới đây, nội dung nào là nói về ý nghĩa sự ra đời của Cương lĩnh chính trị đầu tiên?', 'C. Giải quyết không hoàng về đường lối cách mạng.'),
(7709, 'Câu 7 Đảng khẳng định: võ trang bạo động để giành chính quyền là một nghệ thuật “phải tuân theo khuôn phép nhà binh” tại văn kiện nào?', 'D. Luận cương chính trị (10/1930).'),
(7710, 'Câu 8 Trong số các phương án sau, đâu là phương hướng chiến lược của cách mạng Việt Nam được thể hiện trong Cương lĩnh thành lập Đảng (2/1930)?', 'B. Tư sản dân quyền cách mạng và thổ địa cách mạng để đi tới xã hội cộng sản.'),
(7711, 'Câu 9 Cương lĩnh Chính trị đầu tiên của Đảng đã xác định lực lượng nào là lực lượng chủ yếu của cách mạng tư sản dân quyền?', 'B. Giai cấp công nhân, giai cấp nông dân.'),
(7712, 'Câu 10 Nội dung nào không phải là nhiệm vụ nghiên cứu môn học Đường lối cách mạng của Đảng Cộng sản Việt Nam?', 'B. Làm rõ nền tảng tư tưởng ra đời của Đảng Cộng sản Việt Nam.'),
(7713, 'Câu 11 Cương lĩnh Chính trị đầu tiên của Đảng, đã xác định nhiệm vụ hàng đầu của cách mạng Việt Nam là gì?', 'C. Nhiệm vụ chống đế quốc'),
(7714, 'Câu 12  Tư tưởng chỉ đạo nào sau đây của Đảng được thể hiện trong “Luận cương chính trị” (10/1930)?', 'C. Đề cao nhiệm vụ chống phong kiến, thực hành cách mạng ruộng đất triệt để.'),
(7715, 'Câu 13 Giai cấp, tầng lớp nào giữ vai trò động lực của phong trào cách mạng thời kỳ 1930 - 1931 ở Việt Nam ?', 'B. Công nhân và nông dân.'),
(7716, 'Câu 14 Đảng khẳng định: “Cách mạng Đông Dương là một bộ phận của cách mạng vô sản thế giới, vì vậy phải đoàn kết với giai cấp vô sản trên toàn thế giới, trước hết là giai cấp vô sản Pháp” tại văn kiện nào sau đây?', 'B. Luận cương chính trị (10/1930).'),
(7717, 'Câu 15  Đàng khẳng định: “Đảng là đội tiên phong của giai cấp vô sản, lấy chủ nghĩa Mác - Lênin làm nền tảng tư tưởng, đại biểu chung cho quyền lợi của giai cấp vô sản ở Đông Dương...” tại văn kiện nào?', 'D. Luận cương chính trị (10/1930).'),
(7718, 'Câu 16 Luận cương chính trị của Đảng Cộng sản Đông Dương (10/1930) xác định mâu thuẫn nào đang diễn ra gãy gắt ở Đông Dương?', 'D. Mâu thuẫn giữa thợ thuyền, dân cày, các phần tử lao khổ với địa chủ, tư bản và đế quốC.'),
(7719, 'Câu 17 Thời kỳ nào Đảng ta chủ trương: “Chuyển hình thức tổ chức bí mật, không hợp pháp sang hình thức tổ chức công khai, hợp pháp, nửa hợp pháp...\"?', 'A. Thời kỳ 1936-1939'),
(7720, 'Câu 18 Đường lối giải phóng dân tộc, giải phóng giai cấp trong Cương lĩnh chính trị đầu tiên được hiểu như thế nào?', 'A. Độc lập cho dân tộc, ruộng đất cho dân cày'),
(7721, 'Câu 19 Khái niệm: ‘Tư sản dân quyền cách mạng\' trong Cương lĩnh chính trị đầu tiên của Đảng được hiểu là?', 'A. Cách mạng dân tộc giải phóng.'),
(7722, 'Câu 20  Đảng chủ trương đổi tên “Đảng Cộng sản Việt Nam” thành “ Đảng Cộng sản Đông Dương” tại thời gian nào?', 'A. Hội nghị BCHTW Đảng tháng 10 năm 1930.'),
(7723, 'Câu 21 Hội nghị thành lập Đảng (2/1930) có bao nhiêu đại biểu tham dự', 'B. Có 5 đại biểu'),
(7724, 'Câu 22 Tổ chức “Mặt trận dân chủ Đông Dương” được thành lập vào thời kỳ nào?', 'A. Thời kỳ 1936- 1939'),
(7725, 'Câu 23 Đoàn đại biểu Đảng Cộng sản Đông Dương đi dự Đại hội Quốc tế Cộng sản tại Mạc Tư Khoa (7/1935) do ai dẫn đầu?', 'C. Lê Hồng Phong.'),
(7726, 'Câu 24 Nội dung nào không đúng với đánh giá của Đảng về thái độ chính trị của các giai cấp trong xã hội Việt Nam trong Luận cương chính trị (10/1930)7', 'C. Tiểu tư sản, trí thức có tinh thần yêu nước, có tinh thần cách mạng triệt để, hăng hái tham gia chống đế quốc, phong kiến'),
(7727, 'Câu 25 Về tổng thể, đường lối cách mạng của Đảng bao gồm những nội dung nào?', 'D. Đường lối đối nội và đối ngoại.'),
(7728, 'Câu 26 Sự ra đời của Đảng Cộng sản Việt Nam là sản phẩm của sự kết hợp những yếu tố nào sau dây?', 'C. Chủ nghĩa Mác – Lênin, phong trào công nhân, phongtrào yêu nướC.'),
(7729, 'Câu 27 Quốc tế Cộng sản chỉ rõ: “Vấn đề lập mặt trận thống nhất chống đế quốc có tầm quan trọng đặc biệt” tại Đại hội nào sau đây?', 'D. Đại hội là thứ VII Quốc tế Cộng sản (1935)'),
(7730, 'Câu 28  Lãnh tụ nào của phong trào yêu nước đại diện cho xu hướng bạo động ở nước ta vào đầu thế kỷ XX?', 'C. Phan Bội Châu.'),
(7731, 'Câu 29  Luận cương chính trị của Đảng Cộng sản Đông Dương (10/1930) xác định lực lượng nào là của cách mạng tư sản dân quyền?', 'A. Công nhân, nông dân, các phần tử lao khổ ở đô thị.'),
(7732, 'Câu 30 Yêu cầu cấp thiết trước mắt của nhân dân ta thời kỳ 1936-1939 là gi?', 'B. Đấu tranh đòi quyền sống, quyền tự do dân chủ, cơm áo và hoà bình.'),
(7733, 'Câu 31 Đảng xác định: “Vấn đề thổ địa là cái cốt của cách mạng tư sản dân quyền” tại văn kiện nào sau dây?', 'C. Luận cương chính trị (10/1930)'),
(7734, 'Câu 32 Thời kỳ 1936-1939 Đảng chủ trương thành lập Mặt trận nào sau đây?', 'B. Mặt trận dân chủ Đông Dương'),
(7735, 'Câu 33 Khẩu hiệu: “Ủng hộ Mặt trận nhân dân Pháp”, được Đảng ta đề ra vào thời kỳ nào?', 'D. Thời kỳ 1936 - 1939'),
(7736, 'Câu 34 Hội nghị thành lập Đảng (2/1930) định tên Đảng là gì?', 'C. Đảng Cộng sản Việt Nam.'),
(7737, 'Câu 35 Đảng xác định: “Sau khi cách mạng tư sản dân quyền giành được thắng lợi sẽ tiếp tục phát triển, bỏ qua thời kỳ tư bổn mà tranh đấu thẳng lên con đường XHCN” tại văn kiện nào sau đây?', 'A. Luận cương chính trị (10/1930).'),
(7738, 'Câu 36 Đảng ta khẳng định: “Nhiệm vụ chống đế quốc và chống phong kiến có mối quan hệ không khít với nhau “có đánh đổ được đế quốc chủ nghĩa mới phá được giai cấp địa chủ và làm cách mang | thổ địa được thắng lợi; có phá tan chế độ phong kiến thì mới đánh đổ được đế quốc chủ nghĩa” tại văn kiện nào sau đây?', 'A. Luận cương chính trị (10/1930).'),
(7739, 'Câu 37 Đường lối cách mạng của Đảng được thể hiện qua văn kiện nào dưới đây?', 'B. Cương lĩnh, nghị quyết, chỉ thị'),
(7740, 'Câu 38 Phong trào yêu nước cuối thế kỷ XIX đầu thế kỷ XX theo khuynh hướng nào dẫn tới sự ra đời của Đảng Cộng sản Việt Nam?', 'D. Khuynh hướng cách mạng vô sản.'),
(7741, 'Câu 39 Nội dung nào sau đây nằm trong thư của Nguyễn Ái Quốc kêu gọi đồng bào cả nước (6-6-1941) sau Hội nghị lần thứ tám Ban Chấp hành Trung ương Đảng (5-1941),', 'C. Trong lúc quyền lợi dân tộc giải phóng cao hơn hết thảy, chúng ta phải đoàn kết lại đánh đổ bọn đế quốc và bọn Việt gian đặng cứu giống nòi ra khỏi nước sôi lửa nóng'),
(7742, 'Câu 40 Đảng Cộng sản Việt Nam ra đời là sản phẩm của sự kết hợp giữa những nhân tố nào?', 'B. Chủ nghĩa Mác-Lênin, tư tưởng Hồ Chí Minh với phong trào công nhân và phong trào yêu nước Việt Nam.'),
(7743, 'Câu 41 Hồ Chí Minh kêu gọi: “Trong lúc này quyền lợi dân tộc giải phóng cao hơn hết thảy, chúng ta phải đoàn kết lại đánh đổ bọn đế quốc và bọn Việt gian đặng cứu giống nòi ra khỏi nước sôi lửa bỏng” vào thời gian nào?', 'B. Tháng 5 năm 1941. LỜI KÊU gọi sau Hội nghị BCHTW lần thứ 8'),
(7744, 'Câu 42 Ý nghĩa lịch sử quan trọng đối với cách mạng Việt Nam của phong trào cách mạng 1930-1931?', 'D. Khẳng định trong thực tế quyền lãnh đạo và năng lực lãnh đạo cách mạng của giai cấp vô sản mà đại biểu là Đảng ta'),
(7745, 'Câu 43 Đảng ta đã hoàn chỉnh sự chuyển hướng chỉ đạo chiến lược nhằm giải quyết mục tiêu số một của cách mạng là độc lập dân tộc và đề ra nhiều chủ trương đúng đắn để thực hiện mục tiêu ấy vào thời gian nào sau đây?', 'A. Thời kỳ 1939-1945.'),
(7746, 'Câu 44 Đảng chủ trương thay đổi hình thức đấu tranh “thực hiện hình thức tuyên truyền xung phong, biểu tinh tuần hành, bãi công chính trị, biểu tình phá kho thóc của Nhật để giải quyết nạn đói...” tại văn kiện nào?', 'D. Chỉ thị Nhật - Pháp bắn nhau và hành động của chúng ta (12/3/1945)'),
(7747, 'Câu 45 Thời kỳ nào nhân dân ta phải chịu cảnh thân:“một cổ hai tròng” nô lệ?', 'D. Thời kỳ 1940-1945.'),
(7748, 'Câu 46 Chủ trương của Đảng “Phải luôn chuẩn bị một lực lượng sẵn sàng, nhằm vào cơ hội thuận tiện hơn cả mà đánh lại quân thù...với lực lượng sảẵn có, ta có thể lãnh đạo một cuộc khởi nghĩa từng phần trong từng địa phương cũng có thể giành thắng lợi mà mở đường cho một cuộc tổng khởi nghĩa to lớn” diễn ra vào thời kỳ nào?', 'A. Thời kỳ 1939-1945'),
(7749, 'Câu 47 Quyết định thành lập Mặt trận Việt Minh để đoàn kết, tập hợp lực lượng cách mạng nhằm mục tiêu giải phóng dân tộc tại thời kỳ nào sau đây?', 'A. Thời kỳ 1939- 1945.'),
(7750, 'Câu 48 Chỉ thị “Nhật, Pháp bắn nhau và hành động của chúng ta” (12/3/1945) đã nhận định nội dung nào sau đây?', 'B. Tạo cho cách mạng nước ta những cơ hội tốt làm cho những điều kiện tổng khởi nghĩa nhanh chóng chín muồi.'),
(7751, 'Câu 49 Vua Bảo Đại tuyên bố thoái vị và giao nộp ấn kiếm cho chính quyền cách vào thời gian nào?', 'D. Ngày 30/8/1945'),
(7752, 'Câu 50 Đề cương về văn hóa Việt Nam Năm được Đảng công bố 1943 có nội dung nào sau đây?', 'D. Văn hóa cũng là một trận địa cách mạng, chủ trương xây dựng một nền văn hóa mới theo ba nguyên tắc: dân tộc, khoa học và đại chúng.'),
(7753, 'Câu 51 Đại hội đại biểu lần thứ nhất của Đảng họp ở Ma Cao (3/1935) có ý nghĩa gì?', 'C. Sự phục hồi hệ thống tổ chức của Đảng và phong trào cách mạng quần chúng, tạo điều kiện để bước vào một cao trào cách mạng mới.'),
(7754, 'Câu 52 Ý nghĩa lịch sử nào thể hiện sự chuyển hướng chỉ đạo chiến lược thời kỳ 1939-19457', 'A. Là ngọn cờ dẫn đường cho nhân dân ta tiến lên giành thắng lợi trong sự nghiệp đánh Pháp đuổi Nhật, giành tự do độc lập.'),
(7755, 'Câu 53 Lời kêu gọi của Hồ Chí Minh: “Giờ quyết định cho vận mệnh dân tộc đã đến. Toàn quốc đồng bào hãy đứng dậy đem sức ta mà giải phóng cho ta” vào thời gian nào?', 'C. Tháng 8/1945. Lời kêu gọi toàn quốc đứng lên khởi nghĩA.'),
(7756, 'Câu 54 Thành phần giai cấp nào tham gia Mặt trận Việt Minh thời kỳ 1939-1945?', 'B. Tất cả mọi người dân Việt Nam yêu nước, không phân biệt giai cấp, tôn giáo....'),
(7757, 'Câu 55 Tổng Bí thư Trần Phú trước lúc hy sinh còn căn dặn các đồng chí của mình?', 'C. Hãy giữ vững chí khí chiến đấu'),
(7758, 'Câu 56 Đảng khẳng định: cách mạng Đông Dương vẫn là “cách mạng tư sản dân quyền- phản đế và điền địa - lập chính quyền công nông bằng hình thức Xôviết để dự bị điều kiện đi tới cách mạng XHCN. Song hiện tại chưa tới trình độ trực tiếp đánh đổ đế quốc Pháp, lập chính quyền công nông, giải quyết vấn đề diền địa” vào thời kỳ nào?', 'A. Thời kỳ 1936-1939.'),
(7759, 'Câu 57 Nội dung nào không thông qua tại Quốc dân Đại hội Tân trào (16/8/1945)?', 'A. Quyết định thành lập Việt Nam giải phóng quân.'),
(7760, 'Câu 58 Quyết định xúc tiến chuẩn bị khởi nghĩa vũ trang là nhiệm vụ trung tâm của Đảng và nhân dân trong thời kỳ nào sau đây?', 'C. Thời kỳ 1939-1945.'),
(7761, 'Câu 59 Tổng Bí thư Nguyễn Văn Cừ xuất bản cuốn sách Tự chỉ trích với mục đích gì?', 'D. Thẳng thắn chỉ rõ những sai lầm, khuyết điểm, nêu rõ những bài học cần thiết trong lãnh đạo, chỉ đạo của Đảng.'),
(7762, 'Câu 60 Kẻ thù trước mắt nguy hại nhất của nhân dân Đông Dương cần tập trung đánh đổ là bọn phản động thuộc địa và bè lũ tay sai của chúng tại Văn kiện nào sau đây?', 'C. Văn kiện \"Chung quanh vấn đề chiến sách mới (10/1936).'),
(7763, 'Câu 61 Phong trào cách mạng 1930 – 1931 đã đưa đến sự ra đời?', 'C. Chính quyền Xô Viết'),
(7764, 'Câu 62 Đảng Dân chủ Việt Nam thành lập tháng 6 năm 1944 là Đảng của thành phần xã hội nào?', 'A. Giai cấp tư sản dân tộc và trí thức yêu nước tiến bộ'),
(7765, 'Câu 63 Đảng chủ trương đổi tên các Hội phản để thành Hội cứu quốc vào thời kỳ nào?', 'B. Thời kỳ 1939-1945.'),
(7766, 'Câu 64 Ban chấp hành Trung ương xác định cách mạng Đông Dương vẫn là “cách mạng tư sản dân quyền - phản đế và điền địa - lập chính quyền của công nông bằng hình thức xô viết, để dự bị điều kiện đi tới cách mạng xã hội chủ nghĩa” tại thời kỳ nào sau đây?', 'B. Thời kỳ 1936 1939.'),
(7767, 'Câu 65 Mặt trận: “Việt Nam độc lập đồng minh” đoàn kết, tập hợp lực lượng cách mạng nhằm mục tiêu giải phóng dân tộc là của tổ chức mặt trận nào sau đây?', 'C. Mặt trận Việt Minh.'),
(7768, 'Câu 66 Đảng ta kêu gọi đấu tranh đòi các quyền tự do dân chủ, cơm áo và hòa bình diễn ra vào thời kỳ nào sau đây?', 'D. Thời kỳ 1936-1939.'),
(7769, 'Câu 67 Các chiến khu và căn cứ địa cách mạng, tiêu biểu là căn cứ Bắc Sơn - Vũ Nhai và căn cứ Cao Bằng được thành lập vào thời kỳ nào?', 'D. Thời kỳ 1939-1945.'),
(7770, 'Câu 68 Chủ trương mới nào của Đảng không thuộc thời kỳ cách mạng 1936-1939?', 'C. Chủ trương thực hiện nhiệm vụ giải phóng dân tộc'),
(7771, 'Câu 69 Kẻ thù chính nào của nhân dân ta được xác định trong bản Chỉ thị “Nhật- Pháp bắn nhau và hành động của chúng ta\" (12/3/1945)?', 'D. Phát xít Nhật'),
(7772, 'Câu 70  Chỉ thị “Nhật - Pháp bắn nhau và hành động của chúng ta” phản ánh nội dung của Hội nghị nào?', 'C. Hội nghị Ban Thường vụ Trung ương Đảng (3/1945).'),
(7773, 'Câu 71 Thời kỳ 1939-1945, Đảng xác định nhiệm vụ hàng đầu nào là nhiệm vụ cấp bách nhất của cách mạng Đông Dương?', 'D. Đấu tranh giải phóng dân tộC.'),
(7774, 'Câu 72 “Việt Nam giải phóng quân” được thành lập vào thời kỳ nào?', 'C. Thời kỳ 1939-1945.'),
(7775, 'Câu 73 Đảng chủ trương thay khẩu hiệu: “Đánh đuổi phát xít Nhật, Pháp” bằng khẩu hiệu: “Đánh đuổi phát xít Nhật” tại văn kiện nào?', 'C. Chỉ thị “Nhật Pháp bắn nhau và hành động của chúng ta”(12/3/1945)'),
(7776, 'Câu 74  Nội dung nào được đề cập trong tác phẩm “Tự chỉ trích” của đồng chí Nguyễn Văn Cừ (xuất bản năm 1939)?', 'C. Tổng kết kinh nghiệm cuộc vận động dân chủ của Đảng, về đường lối xây dựng Mặt trận dân chủ Đông Dương về công tác xây dựng Đảng.'),
(7777, 'Câu 75 Thời kỳ 1936-1939, Đảng xác định nhiệm vụ nào là nhiệm vụ trước mắt của cách mạng Đông Dương?', 'D. Chống phát xít, chống phản động thuộc địa và tay sai,chống chiến tranh đế quốc, đòi tự do dân chủ, cơm áo và hoà bình.'),
(7778, 'Câu 1 Đại hội đại biểu toàn quốc lần thứ VII (6/1991) của Đảng đã nêu ra phương châm thực hiện về đối ngoại là gì?', 'D. Việt Nam muốn là bạn với tất cả các nước trong cộng đồng thế giới, phấn đấu vì hòa bình, độc lập và phát triển.'),
(7779, 'Câu 2 Đại hội Đại biểu toàn quốc nào của Đảng xác định: Nhiệm vụ đề ra cho chặng đường đầu của thời kỳquá độ là chuẩn bị tiền đề cho công nghiệp hoá đã cơ bản hoàn thành, cho phép chuyển sang thời kỳ mới đẩy mạnh công nghiệp hoá, hiện đại hoá đất nước?', 'C. Đại hội đại biểu toàn quốc lần thứ VIII (6/1996)'),
(7780, 'Câu 3 Đại hội nào của Đảng đã chủ trương coi nông nghiệp là mặt trận hàng đầu?', 'A. Đại hội đại biểu toàn quốc lần thứ V (3/1982)'),
(7781, 'Câu 4 Tại Hội nghị nào của Đảng lần đầu tiên khẳng định xây dựng Nhà nước pháp quyền Việt Nam của nhân dân, do nhân dân, vì nhân dân?', 'C. Hội nghị giữa nhiệm kỳ (1-1994)'),
(7782, 'Câu 5 Đại hội nào của Đảng được đánh giá là Đại hội khởi xướng đường lối đổi mới toàn diện?', 'D. Đại hội đại biểu toàn quốc lần thứ VI (12/1986)'),
(7783, 'Câu 6 Chủ trương xây dựng và tổ chức thực hiện ba chương trình kinh tế lớn về lương thực, thực phẩm; hàng tiêu dùng; hàng xuất khẩu được Đảng đề ra tại Đại hội nào?', 'C. Đại hội đại biểu toàn quốc lần thứ VI (12/1986)'),
(7784, 'Câu 7 Chủ trương đổi mới công tác kế hoạch hoá và cải tiến một cách cơ bản chính sách kinh tế để làm cho sản xuất \"bung ra\" được nêu lên ở Hội nghị nào của Trung ương Đảng, khoá IV?', 'C. Hội nghị lần thứ năm (12-1978)'),
(7785, 'Câu 8 Cuộc Tổng tuyển cử bầu Quốc hội chung của nước Việt Nam thống nhất được tiến hành vào thời gian nào?', 'B. 25-4-1976'),
(7786, 'Câu 9 Kỳ họp nào của Đảng quyết định đặt tên nước ta là nước Cộng hoà Xã hội chủ nghĩa Việt Nam?', 'B. Kỳ họp thứ nhất của Quốc hội nước Việt Nam (từ 24/6 - 3/7/1976)'),
(7787, 'Câu 10 Hội nghị Trung ương 7 (7-1994) khóa VII của Đảng đưa ra chủ trương về vấn đề gì?', 'A. Phát triển công nghiệp, công nghệ và xây dựng giai cấp công nhân trong giai đoạn mới.'),
(7788, 'Câu 11 Đảng xác định: “Mục tiêu tổng quát phải đạt tới, khi kết thúc thời kỳ quá độ là xây dựng xong về cơ bản những cơ sở kinh tế của chủ nghĩa xã hội, với kiến trúc thượng tầng về chính trị và tư tưởng, văn hóa phù hợp, làm cho nước ta trở thành một nước xã hội chủ nghĩa phồn vinh”. Chủ trương trên được nêu ra tại văn kiện nào của Đảng?', 'B. Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (1991)'),
(7789, 'Câu 12 Đại hội đại biểu toàn quốc lần thứ VI (12/1986) của Đảng đã chỉ rõ những trong thời kỳ 1975-1986 Đảng đã mắc những sai lầm, khuyết điểm nào?', 'A. Đó là những sai lầm nghiêm trọng và kéo dài về chủ trương, chính sách lớn, sai lầm v chiến lược và tổ chức thực hiện.'),
(7790, 'Câu 13 Hội nghị Trung ương nào của Đảng lần đầu tiên đưa ra chủ trương tự đổi mới, tự chỉnh đốn Đảng?', 'A. Hội nghị Trung ương 3 (6-1992)'),
(7791, 'Câu 14 Nội dung của Chỉ thị số 228-CT/TW của Bộ Chính trị Trung ương Đảng nói về vấn đề gì?', 'C. Nêu rõ tầm quan trọng của cuộc Tổng tuyển cử và giao trách nhiệm cho các cấp ủy lãnh đạo cuộc bầu cử.'),
(7792, 'Câu 15 Việt Nam và Trung Quốc bình thường hóa quan hệ vào thời gian nào?', 'B. Từ tháng 11-1991'),
(7793, 'Câu 16 Đại hội Đại biểu toàn quốc lần thứ VI (12/1986) của Đảng đã đề ra bao nhiêu phương hướng lớn phát triển kinh tế?', 'C. 5 phương hướng'),
(7794, 'Câu 17 Hội nghị Trung ương 2 (4/1987) của Đảng đưa ra chủ trương về vấn đề gì?', 'C. Đề ra những chủ trương, biện pháp cấp bách về phân phối lưu thông, thực hiện bốn gi'),
(7795, 'Câu 18 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội do Đại hội VII thông qua xác định xã hội xã hội chủ nghĩa mà nhân dân ta xây dựng gồm mấy đặc trưng cơ bản?', 'B. 6 đặc trưng'),
(7796, 'Câu 19 “Đảng Cộng sản Việt Nam lấy chủ nghĩa Mác-Lênin và tư tưởng Hồ Chí Minh là nền tảng tư tưởng và kim chỉ nam cho hành động, tiếp thu tinh hoa trí tuệ của dân tộc và nhân loại, nắm vững quy luật khách quan và thực tiễn của đất nước để đề ra Cương lĩnh chính trị, đường lối cách mạng đúng đắn, phù hợp với yêu cầu, nguyện vọng của nhân dân”. Chủ trương trên được nêu ra tại Đại hội đại biểu toàn quốc lần thứ mấy của Đảng?', 'B. Đại hội đại biểu toàn quốc lần thứ VII (6/1991)'),
(7797, 'Câu 20 Hội nghị nào của Đảng đã kịp thời phân tích tình hình các nước xã hội chủ nghĩa, sự phá hoại của chủ nghĩa đế quốc, đề ra nhiệm vụ của Đảng ta?', 'B. Hội nghị Trung ương 8 (Khóa VI) (3-1990).'),
(7798, 'Câu 21 Đại hội nào của Đảng được gọi là Đại hội \"Trí tuệ, đổi mới, dân chủ, kỷ cương và đoàn kết\"?', 'A. Đại hội đại biểu toàn quốc lần thứ VII (6/1991)'),
(7799, 'Câu 22 Việt Nam và Hoa Kỳ bình thường hóa quan hệ vào thời gian nào?', 'D. Từ tháng 7-1995'),
(7800, 'Câu 23 Quyết định số 25-CP (1-1981) của Chính phủ đưa ra chủ trương nào sau đây?', 'D. Quyền chủ động sản xuất kinh doanh và quyền tự chủvề tài chính của các xí nghiệp'),
(7801, 'Câu 24 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội được thông qua trong Đại hội nào của Đảng?', 'A. Đại hội đại biểu toàn quốc lần thứ VII (6/1991)'),
(7802, 'Câu 25 Nội dung nào dưới đây được Đại hội đại biểu toàn quốc lần thứ IV (12/1976) của Đảng xác định đường lối xây dựng, phát triển kinh tế.', 'D. Đẩy mạnh công nghiệp hoá xã hội chủ nghĩa bằng ưu tiên phát triển công nghiệp nặng hợp lý trên cơ sở phát triển nông nghiệp và công nghiệp nhẹ, xây dựng cơ cấu kinh tế nông nghiệp; kết hợp kinh tế trung ương với kinh tế địa phương, kết hợp phát triển lực lượng sản xuất.'),
(7803, 'Câu 26 Chủ trương hoàn thành thống nhất nước nhà, đưa cả nước tiến nhanh, tiến mạnh, tiến vững chắc lên chủ nghĩa xã hội được đề ra tại hội nghị nào của Đảng?', 'A. Hội nghị lần thứ 24 Ban Chấp hành Trung ương Đảng khóa III (8 -1975)'),
(7804, 'Câu 27 “Chiến lược ổn định và phát triển kinh tế-xã hội đến năm 2000 được thông qua tại Đại hội đại biểu toàn quốc lần thứ mấy của Đảng?', 'B. Đại hội đại biểu toàn quốc lần thứ VII (6/1991)'),
(7805, 'Câu 28 Nghị quyết số 07 của Bộ Chính trị ban hành ngày 17-11-1993 đưa ra chủ trương về vấn đề gì?', 'B. Đại đoàn kết dân tộc và tăng cường Mặt trận Dân tộc thống nhất'),
(7806, 'Câu 29 Đại hội đại biểu toàn quốc lần thứ IV (12/1976) xác định các đặc trưng cơ bản về chủ nghĩa xã hội ở nước ta là nội dung nào sau đây?', 'A. Xây dựng chế độ làm chủ tập thể xã hội chủ nghĩa, nền sản xuất lớn, nền văn hoá mới, con người mới xã hội chủ nghĩa; coi chuyên chính vô sản, phát huy quyền làm chủ tập thể của nhân dẫn lao động là công cụ để xây dựng chủ nghĩa xã hội.'),
(7807, 'Câu 30 Đại hội Đại biểu toàn quốc lần thứ VI (12/1986) của Đảng đã đề ra đường lối đổi mới toàn diện, trong đó trọng tâm là đổi mới về lĩnh vực nào ?', 'C. Kinh tế'),
(7808, 'Câu 31 \"Nước ta đã ra khỏi khủng hoảng kinh tế- xã hội, nhưng một số mặt còn chưa vững chắc\" là đánh giá tổng quát của Đại hội nào?', 'C. Đại hội Đại hội đại biểu toàn quốc lần thứ VIII (1996)'),
(7809, 'Câu 32 Sự kiện nào sau đây của Đảng được đánh giá là bước đột phá thứ ba về đổi mới kinh tế, đồng thời cũng là bước quyết định cho sự ra đời của đường lối đổi mới của Đảng?', 'D. Hội nghị Bộ Chính trị khoá V (8-1986)'),
(7810, 'Câu 33 Hội nghị nào của Ban Chấp hành Trung ương Đảng khoá V quyết định phải dứt khoát xoá bỏ cơ chế tập trung quan liêu bao cấp, thực hiện hạch toán kinh doanh xã hội chủ nghĩa?', 'A. Hội nghị lần thứ tám (6-1985)'),
(7811, 'Câu 34 Kỳ họp thứ nhất của Quốc hội nước Việt Nam thống nhất (Quốc hội khoá VI) được tổ chức trong thời gian nào?', 'C. Từ 24/6 đến 3/7/1976'),
(7812, 'Câu 35 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội do Đại hội VII thông qua xác định bao nhiêu phương hướng lớn xây dựng chủ nghĩa xã hội?', 'A. 7 phương hướng'),
(7813, 'Câu 36  Hội nghị hiệp thương chính trị giữa đoàn đại biểu miền Bắc và đoàn đại biểu miền Nam đã họp ở đâu?', 'C. Sài Gòn'),
(7814, 'Câu 37 Việt Nam gia nhập Hiệp hội các quốc gia Đông Nam Á (ASEAN) vào thời gian nào?', 'B. Ngày 28-7-1995'),
(7815, 'Câu 38 Đại hội Đại biểu toàn quốc lần thứ VIII (1996) của Đảng đề ra mục tiêu \"đưa đất nước cơ bản trở thành một nước công nghiệp\" vào năm nào?', 'D. Năm 2020'),
(7816, 'Câu 39 Kế hoạch 5 năm lần thứ II (1976 - 1980) do đại hội đại biểu toàn quốc lần thứ mấy của Đảng thông qua?', 'C. Đại hội đại biểu toàn quốc lần thứ IV (12/1976)'),
(7817, 'Câu 40 Chỉ thị 100 CT/TW của ban Bí thư Trung ương Đảng (1-1981) đưa ra chủ trương nào sau đây?', 'B. Khoán sản phẩm đến nhóm và người lao động trong hợp tác xã nông nghiệp'),
(7818, 'Câu 41 Quan điểm về công nghiệp hóa trong thời kỳ mới của Đại hội Đại biểu toàn quốc lần thứ VIII (6/1996) của Đảng đưa ra xác định yếu tố nào là yếu tố cơ bản cho sự phát triển nhanh và bền vững?', 'A. Con người'),
(7819, 'Câu 42 Đại hội nào của Đảng diễn ra trong bối cảnh: Khu vực Đông Nam Á, châu Á-Thái Bình Dương trở thành khu vực phát triển kinh tế năng động nhưng tiềm ẩn những nhân tố gây mất ổn định. Sau 15 năm đổi mới, Việt Nam đạt được nhiều thành tựu quan trọng, tạo thế và lực mới nhưng nền kinh tế phát triển chưa vững chắc, hiệu quả và sức cạnh tranh thấp?', 'C. Đại hội đại biểu toàn quốc lần thứ IX (4/2001)'),
(7820, 'Câu 43 Hội nghị Trung ương 5 khóa X (7-2007) của Đảng đã đưa ra chủ trương về vấn đề gì?', 'B. Chủ trương tăng cường công tác kiểm tra, giám sát của Đảng'),
(7821, 'Câu 44 Nghị quyết về chủ động ứng phó với biến đổi khí hậu, tăng cường quản lý tài nguyên và bảo vệ môi trường được ban hành tại cuộc họp nào của Đảng?', 'B. Hội nghị Trung ương 7 khóa XI (6-2016)'),
(7822, 'Câu 45 Quan điểm về công nghiệp hóa trong thời kỳ mới của Đại hội Đại biểu toàn quốc lần thứ VIII (6/1996) của Đảng xác định công nghiệp hóa, hiện đại hóa là nhiệm vụ của ai?', 'B. Công nghiệp hóa, hiện đại hóa là sự nghiệp của toàn dân, của mọi thành phần kinh tế, trong đó kinh tế nhà nước giữ vai trò chủ đạo'),
(7823, 'Câu 46 Mặt trận Tổ quốc Việt Nam, các đoàn thể nhân dân có vai trò như thế nào trong hệ thống chính trị của Việt Nam?', 'D. Có vai trò rất quan trọng trong sự nghiệp đại đoàn kết toàn dân tộc xây dựng và bảo vệ Tổ quốc.'),
(7824, 'Câu 47 Đại hội nào của Đảng diễn ra trong bối cảnh tình hình thế giới, khu vực diễn biến nhanh, rất phức tạp, khó lường. Đất nước qua 30 năm đổi mới, thế và lực tăng lên rõ rệt, có cả những thuận lợi, thời cơ đan xen với những khó khăn, thách thức gay gắt?', 'B. Đại hội đại biểu toàn quốc lần thứ XII (1/2016)'),
(7825, 'Câu 48 Chiến lược phát triển bền vững kinh tế biển Việt Nam đến năm 2030, tầm nhìn đến năm 2045 được đưa ra tại cuộc họp nào của Đảng?', 'A. Hội nghị Trung ương 8 khóa XII (10-2018)'),
(7826, 'Câu 49 Nghị quyết nào của Đảng được xem như Tuyên ngôn văn hóa của Đảng trong thời kỳ đẩy mạnh công nghiệp hóa, hiện đại hóa?', 'B. Nghị quyết Trung ương 5 khóa VIII'),
(7827, 'Câu 50 Đảng Cộng sản Việt Nam có vị trí như thế nào trong hệ thống chính trị của Việt Nam?', 'D. Đảng lãnh đạo hệ thống chính trị, đồng thời là bộ phận của hệ thống ấy.'),
(7828, 'Câu 51 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (bổ sung, phát triển năm 2011) xác định phương châm đối ngoại của Đảng có nội dung như thế nào?', 'A. Thực hiện nhất quán đường lối đối ngoại độc lập, tự chủ, hòa bình, hợp tác và phát triển; đa phương hóa, đa dạng hóa quan hệ, chủ động và tích cực hội nhập quốc tế; là bạn, đối tác tin cậy và thành viên có trách nhiệm trong cộng đồng quốc tế; vì lợi ích quốc gia, dân tộc, vì một nước Việt Nam xã hội chủ nghĩa giàu mạnh'),
(7829, 'Câu 52 Hội nghị Trung ương nào của Đảng đã ban hành Nghị quyết về xây dựng đội ngũ trí thức trong kỳ đẩy mạnh công nghiệp hoá, hiện đại hoá đất nước và hội nhập kinh tế quốc tế?', 'C. Hội nghị Trung ương 7, khóa X (7-2008)'),
(7830, 'Câu 53 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (bổ sung, phát triển năm 2011) đã bổ sung cần nắm vững và giải quyết tốt bao nhiêu mối quan hệ lớn?', 'B. 8'),
(7831, 'Câu 54 Việt Nam tham gia Diễn đàn kinh tế Á-Âu (ASEM) vào thời gian nào?', 'D. Năm 1996'),
(7832, 'Câu 55 Hội nghị Trung ương 5 (5-2017) khóa XII của Đảng đưa ra chủ trương về vấn đề gì?', 'B. Chủ trương tiếp tục hoàn thiện thể chế kinh tế thị trường định hướng xã hội chủ nghĩa.'),
(7833, 'Câu 56 Nghị quyết về phát triển khoa học và công nghệ phục sự nghiệp công nghiệp hiện hóa trong điều kiện kinh tế thị trường định hướng xã hội chủ nghĩa và hội nhập quốc tế được ban hành tại cuộc họp nào của Đảng?', 'A. Hội nghị Trung ương 6, khóa XI (10-2012)'),
(7834, 'Câu 57 Việt Nam gia nhập tổ chức Hợp tác kinh tế Châu Á-Thái Bình Dương (APEC) vào thời gian nào?', 'D. Ngày 14-11-1998'),
(7835, 'Câu 58 Hội nghị Trung ương nào của Đảng đã ban hành Nghị quyết về xây dựng giai cấp công nhân trong thời kỳ đẩy mạnh công nghiệp hoá, hiện đại hoá đất nước?', 'B. Hội nghị Trung ương 6, khóa X (8-2007)'),
(7836, 'Câu 59 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xãhội (bổ sung, phát triển năm 2011) có bổ sung thêm hai đặc trưng nào về mô hình chủ nghĩa xã hội ở nước ta?', 'A. “Dân giàu, nước mạnh, dân chủ, công bằng, văn minh”; “có Nhà nước pháp quyền xã hội chủ nghĩa của nhân dân, do nhân dân, vì nhân dân do Đảng Cộng sản lãnh đạo.'),
(7837, 'Câu 60 Đại hội nào của Đảng đã đưa ra Chiến lược phát triển kinh tế-xã hội 2011-2020?', 'D. Đại hội đại biểu toàn quốc lần thứ XI (1/2011)'),
(7838, 'Câu 61 Hội nghị Trung ương 4 (1-2012) của Đảng đưa ra chủ trương về vấn đề gì?', 'B. Chủ trương xây dựng hệ thống kết cấu hạ tầng đồng bộ nhằm đưa nước ta cơ bản trở thành nước công nghiệp theo hướng hiện đại vào năm 2020'),
(7839, 'Câu 62 Đại hội Đại biểu toàn quốc lần thứ VIII (6/1996) của Đảng đưa ra bao nhiêu quan điểm về công nghiệp hóa trong thời kỳ mới?', 'D. 6 quan điểm'),
(7840, 'Câu 63 Nghị quyết về phát huy sức mạnh đại đoàn kết toàn dân tộc vì “Dân giàu, nước mạnh, xã hội công bằng, dân chủ, văn minh” được ban hành tại cuộc họp nào của Đảng?', 'C. Hội nghị Trung ương 7 (3-2003)'),
(7841, 'Câu 64  Đại hội Đại biểu toàn quốc lần thứ VIII (6/1996) của Đảng nêu ra bao nhiêu bài học chủ yếu qua 10 năm đổi mới?', 'A. 6 bài học'),
(7842, 'Câu 65 Nội dung của Hội nghị Trung ương 3 (9-2001) của Đảng bàn về vấn đề gì?', 'C. Chỉ đạo sắp xếp, đổi mới, phát triển và nâng cao hiệu quả doanh nghiệp nhà nước, tạc phát triển mới, tạo thế và lực cho các doanh nghiệp nhà nước hoạt động có hiệu quả h thời kỳ đẩy mạnh công nghiệp hoá, hiện đại hoá và hội nhập kinh tế quốc tế.'),
(7843, 'Câu 66 Đại hội nào của Đảng diễn ra trong bối cảnh tình hình thế giới tiếp tục diễn biến phức tạp. Hoà bình, hợp tác và phát triển vẫn là xu thế lớn, nhưng chiến tranh cục bộ, xung đột vũ trang, khủng bố, tranh chấp lãnh thổ diễn biến phức tạp. Thiên tai, dịch bệnh, khủng hoảng, suy thoái kinh tế toàn cầu, ô nhiễm môi trường, biến đổi khí hậu xảy ra nghiêm trọng trên toàn thế giới.', 'B. Đại hội đại biểu toàn quốc lần thứ XI (1/2011)'),
(7844, 'Câu 67 Hội nghị Trung ương 6 khóa X (1-2008) của Đảng đã đưa ra chủ trương về vấn đề gì?', 'D. Chủ trương tăng cường sự lãnh đạo của Đảng đối với công tác đấu tranh phòng, chống tham nhũng, lãng phí.'),
(7845, 'Câu 68 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (bổ sung, phát triển năm 2011) chỉ rõ phương hướng cơ bản xây dựng chủ nghĩa xã hội ở nước ta: đẩy mạnh công nghiệp hoá, hiện đại hoá đất nước gắn với nhiệm vụ nào?', 'A. Phát triển kinh tế tri thức, bảo vệ tài nguyên, môi trường.'),
(7846, 'Câu 69 Hội nghị nào của Đảng xác định giáo dục-đào tạo cùng với khoa học và công nghệ là quốc sách hàng đầu, là nhân tố quyết định tăng trưởng kinh tế và phát triển xã hội?', 'B. Hội nghị Trung ương 2 khóa VIII (12-1996)'),
(7847, 'Câu 70 Đại hội nào của Đảng được đánh giá là Đại hội “Đoàn kết-Dân chủ-Kỷ cương-Đổi mới”?', 'D. Đại hội đại biểu toàn quốc lần thứ XII (1/2016)'),
(7848, 'Câu 71 Hội nghị Trung ương 6, khóa X (5-2012) của Đảng đưa ra chủ trương về vấn đề gì?', 'A. Ban hành Kết luận về tiếp tục sắp xếp, đổi mới, nâng cao hiệu quả doanh nghiệp nhà nước.'),
(7849, 'Câu 72 : Đại hội đại biểu toàn quốc lần thứ IX (4/2001) đã xác định mô hình kinh tế tổng quát của nước ta trong thời kỳ quá độ đi lên chủ nghĩa xã hội là gì?', 'A. Kinh tế thị trường định hướng xã hội chủ nghĩa'),
(7850, 'Câu 73 Hội nghị Trung ương 6 lần 2 (2-1999) đã ra Nghị quyết về vấn đề gì?', 'B. Về một số vấn đề cơ bản và cấp bách trong công tác xây dựng Đảng'),
(7851, 'Câu 74 Cuộc vận động “Học tập và làm theo tấm gương đạo đức Hồ Chí Minh” trong toàn Đảng, toàn dân được Bộ Chính trị bắt đầu tổ chức vào thời gian nào?', 'D. Tháng 11-2006'),
(7852, 'Câu 75 Đại hội đại biểu toàn quốc lần thứ IX (4/2001) đã xác định phương châm đối ngoại của Đảng ta như thế nào?', 'A. Việt Nam sẵn sàng là bạn, là đối tác tin cậy của các nước trong cộng đồng quốc tế, phấn đấu vì hoà bình, độc lập và phát triển.'),
(7853, 'Câu 76 Nghị quyết về tiếp tục đổi mới mô hình tăng trưởng, nâng cao chất lượng tăng trưởng, năng suất lao động và sức cạnh tranh của nền kinh tế được ban hành tại cuộc họp nào của Đảng?', 'C. Hội nghị Trung ương 4, khóa XII (10-2016)'),
(7854, 'Câu 77  Hội nghị Trung ương 4 khóa VIII (12-1997) của Đảng đã bầu đồng chí nào giữ chức vụ Tổng Bí thu?', 'B. Đồng chí Lê Khả Phiêu'),
(7855, 'Câu 78 Chiến lược phát triển kinh tế-xã hội 2001-2010 được Đảng thông qua tại Đại hội đại biểu toàn quốc. lần thứ mấy?', 'A. Đại hội đại biểu toàn quốc lần thứ IX (4/2001)'),
(7856, 'Câu 79 Hội nghị Trung ương 3 (6-1997) đã thông qua Nghị quyết về vấn đề gì?', 'C. Về phát huy quyền làm chủ của nhân dân, tiếp tục xây dựng Nhà nước Cộng hoà Xã hội chủ nghĩa Việt Nam trong sạch, vững mạnh'),
(7857, 'Câu 80 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (bổ sung, phát triển năm 2011) chỉ rõ bao nhiêu phương hướng cơ bản xây dựng chủ nghĩa xã hội ở nước ta?', 'C. 8 phương hướng'),
(7858, 'Câu 81 Chủ trương phát triển kinh tế tư nhân trở thành một động lực quan trọng của nền kinh tế thị trường định hướng xã hội chủ nghĩa được đưa ra tại cuộc họp nào của Đảng?', 'C. Hội nghị Trung ương 5, khóa XII (5-2017)'),
(7859, 'Câu 1 Là một ngành của khoa học lịch sử, lịch sử Đảng có các chức năng của khoa học lịch sử đồng thời có chức năng nổi bật là:', 'A. Chức năng nhận thức, giáo dục, dự báo và phê phán.'),
(7860, 'Câu 2 Năm 1933, tác phẩm “Sơ thảo lịch sử phong trào Đông Dương” là bản nghiên cứu tổng hợp đầu tiên về lịch sử Đảng. Tác phẩm này của ai?', 'C. Hà Huy Tập.'),
(7861, 'Câu 3 Điền vào chỗ trống: “Đảng lấy chủ nghĩa Mác - Lênin, … làm nền tảng tư tưởng, kim chỉ nam cho hành động”', 'B. Tư tưởng Hồ Chí Minh.'),
(7862, 'Câu 4 Nghiên cứu, học tập lịch sử Đảng cần quán triệt tính đảng và tính khoa học. Vậy “tính khoa học” trong nghiên cứu lịch sử Đảng là gì?', 'A. Là sự phản ánh kết quả nghiên cứu sự vật, hiện tượng, sự kiện lịch sử phải đạt đến chân lý khách quan dựa trên luận cứ khoa học, tôn trọng hiện thực lịch sử.'),
(7863, 'Câu 5 Tại sao khi nghiên cứu khoa học lịch sử Đảng Cộng sản Việt Nam lại cần phải nhận thức và vận dụng chủ nghĩa duy vật lịch sử?', 'C. Để nhận thức tiến trình cách mạng do Đảng Cộng sản Việt Nam lãnh đạo.'),
(7864, 'Câu 6 Một trong những nhiệm vụ hàng đầu khi nghiên cứu khoa học Lịch sử Đảng Cộng sản Việt Nam là:', 'B. Khẳng định, chứng minh giá trị khoa học của những mục tiêu chiến lược và sách lược cách mạng mà Đảng đề ra trong cương lĩnh.'),
(7865, 'Câu 7 Trong nghiên cứu lịch sử Đảng, khi xem xét, đối chiếu các hiện tượng lịch sử trong hình thức tổng quát nhằm vạch ra bản chất, quy luật trong sự vận động của sự vật thì đó là phương pháp nghiên cứu nào?', 'A. Phương pháp logic.'),
(7866, 'Câu 8 Đối tượng nghiên cứu của Khoa học Lịch sử Đảng Cộng sản Việt Nam là:', 'B. Sự ra đời, phát triển, hoạt động lãnh đạo của Đảng qua các thời kỳ.'),
(7867, 'Câu 9 Việc tiến hành thảo luận, trao đổi các vấn đề do giảng viên đưa ra để có thể hiểu rõ hơn nội dung của môn học lịch sử Đảng Cộng sản Việt Nam thì được gọi là:', 'D. Phương pháp làm việc nhóm.'),
(7868, 'Câu 10 Nghiên cứu, học tập lịch sử Đảng cần quán triệt tính đảng và tính khoa học. Vậy “tính đảng” là gì?', 'D. Là đứng trên lập trường của chủ nghĩa Mác-Lênin, tư tưởng Hồ Chí Minh để nhận thức lịch sử một cách khoa học, đúng đắn, luôn luôn kế thừa và phát triển sáng tạo'),
(7869, 'Câu 11 Cần phải coi trọng phương pháp tổng kết thực tiễn lịch sử gắn với nghiên cứu lý luận trong nghiên cứu lịch sử Đảng để:', 'B. Làm rõ kinh nghiệm, bài học, quy luật phát triển của cách mạng Việt Nam.'),
(7870, 'Câu 12 Bộ môn Lịch sử Đảng Cộng sản Việt Nam chính thức được đưa vào giảng dạy, học tập chính thức trong các trường đại học, cao đẳng, trung cấp chuyên nghiệp khi nào?', 'C. Từ những năm 60 của TK XX');
INSERT INTO `questions` (`id`, `question_name`, `correct_answer`) VALUES
(7871, 'Câu 13 Trong phương pháp nghiên cứu, học tập môn học Lịch sử Đảng Cộng sản Việt Nam cần phải dựa trên phương pháp luận khoa học Mácxít, đồng thời phải nắm vững chủ nghĩa nào dưới đây để xem xét và nhận thức lịch sử một cách khách quan, trung thực và đúng quy luật?', 'B. Chủ nghĩa duy vật biện chứng và chủ nghĩa duy vật lịch sử.'),
(7872, 'Câu 14 Bản cương lĩnh đầu tiên của Đảng Cộng sản Việt Nam ra đời năm bao nhiêu?', 'D. 1930.'),
(7873, 'Câu 15 Đảng lãnh đạo cách mạng giải phóng dân tộc, xây dựng đất nước bằng…', 'C. Cương lĩnh, đường lối, chủ trương, chính sách lớn.'),
(7874, 'Câu 16 Thực dân Pháp xâm lược Việt Nam vào thời gian nào?', 'A. 1858'),
(7875, 'Câu 17 Khoa học lịch sử Đảng có mấy nhiệm vụ trọng tâm:', 'D. 4'),
(7876, 'Câu 18 Nghiên cứu, học tập lịch sử Đảng cần quán triệt tính đảng và tính khoa học. Vậy “tính đảng” là gì?', 'C. Là đứng trên lập trường chủ nghĩa Mác - Lênin, tư tưởng Hồ Chí Minh để nhận thức lịch sử một cách khoa học, đúng đắn, luôn luôn kế thừa và phát triển sáng tạo.'),
(7877, 'Câu 1 Tổ chức An Nam Cộng sản Đảng được thành lập khi nào và ở đâu?', 'C. 11/1929 tại Khánh Hội (Sài Gòn).'),
(7878, 'Câu 2 Ba cuộc khởi nghĩa Bắc Sơn, Nam Kì, binh biến Đô Lương đã để lại bài học kinh nghiệm lớn nhất là gì?', 'A. Bài học về thời cơ trong khởi nghĩa giành chính quyền.'),
(7879, 'Câu 3 Cao trào 1930-1931 diễn ra đỉnh cao ở địa phương nào?', 'D. Nghệ Tĩnh.'),
(7880, 'Câu 4 BTrong các nhà yêu nước tiêu biểu cho phong trào yêu nước cuối thế kỷ XIX đầu thế kỷ XX được kể sau đây, ai là người tiêu biểu cho phong trào Cần Vương?', 'A. Hàm Nghi.'),
(7881, 'Câu 5 Từ năm 1936, những chính sách tích cực của chính phủ mặt trận nhân Pháp ở Đông Dương là:', 'C. cở sở để Đảng đề ra hình thức đấu tranh trong thời kỳ 1936-1939'),
(7882, 'Câu 6 Trong những điểm sau đây nói về “hoàn cảnh quốc tế cuối thế kỷ XIX đầu thế kỷ XX đã tác động đến sự ra đời của Đảng Cộng sản Việt Nam”, điểm tác động nào nói đến ảnh hưởng của chủ nghĩa Mác - Lênin?', 'B. Chủ nghĩa Mác - Lênin đã lay chuyển lôi cuốn quần chúng nhân dân và những phần tử ưu tú tích cực ở các nước thuộc địa vào phong trào cộng sản.'),
(7883, 'Câu 7 Trong những điểm sau đây nói về chính sách cai trị của thực dân Pháp, điểm nào thuộc về chính sách cai trị về chính trị?', 'D. Chia Việt Nam ra thành ba xứ để cai trị: Bắc Kỳ, Trung Kỳ, Nam Kỳ.'),
(7884, 'Câu 8 Mặt trận Việt Minh chính thức thành lập ngày nào?', 'C. 1921.'),
(7885, 'Câu 9 Tổ chức cộng sản nào ra đời đầu tiên ở Việt Nam?', 'B. Đông Dương cộng sản Đảng.'),
(7886, 'Câu 10 Hội liên hiệp các dân tộc thuộc địa được thành lập năm nào?', 'C. 19/5/1941.'),
(7887, 'Câu 11 Mục tiêu cụ thể trước mắt của cao trào cách mạng giai đoạn 1936 - 1939 là gì?', 'A. Các quyền dân chủ đơn sơ'),
(7888, 'Câu 12 Bản hiệp ước cuối cùng mà triều đình nhà Nguyễn kí với thực dân Pháp, chính thức biến Việt Nam thành thuộc địa nửa phong kiến là:', 'A. ,Pa tơ nốt.'),
(7889, 'Câu 13 Sau chiến tranh thế giới thứ nhất, ở Việt Nam có những giai cấp nào?', 'D. Địa chủ phong kiến, nông dân, tư sản, tiểu tư sản và công nhân.'),
(7890, 'Câu 14 Hội nghị thành lập Đảng Cộng sản Việt Nam diễn ra vào thời gian nào?', 'D. Từ 06/01/1930 đến 07/02/1930.'),
(7891, 'Câu 15 BNguyễn Ái Quốc đọc bản “Sơ thảo luận cương về vấn đề dân tộc và thuộc địa” của Lênin khi nào và ở đâu?', 'C. 7/1920 tại Pháp.'),
(7892, 'Câu 16  Đoạn trích sau trong văn bản nào: “Trong giai đoạn này, quyền lợi của giai cấp công nhân và nhân dân lao động và của dân tộc là một. Chính vì Đảng Lao động Việt Nam là Đảng của giai cấp công nhân và nhân dân lao động, cho nên nó phải là Đảng của dân tộc Việt Nam”?', 'B. Báo cáo chính trị tại Đại hội đại biểu lần thứ II (1951).'),
(7893, 'Câu 17 Trong các nhà yêu nước tiêu biểu cho phong trào yêu nước cuối thế kỷ XIX đầu thế kỷ XX được kể sau đây, ai là người tiêu biểu cho xu hướng bạo động cách mạng?', 'D. Phan Bội Châu.'),
(7894, 'Câu 18 Phong trào của vua Hàm Nghi thuộc khuynh hướng nào trong các khuynh hướng sau?', 'B. Phong trào yêu nước theo khuynh hướng phong kiến.'),
(7895, 'Câu 19 Trước chiến tranh thế giới thứ nhất, ở Việt Nam có những giai cấp nào?', 'D. Địa chủ phong kiến, nông dân và công nhân.'),
(7896, 'Câu 20 Điều nào không phải chính sách của chính phủ Mặt trận nhân dân Pháp năm 1936 đối với các thuộc địa?', 'B. Giải quyết vấn đề ruộng đất cho nhân dân.'),
(7897, 'Câu 21 Quốc dân Đại hội Tân trào tháng 8-1945 đã KHÔNG quyết định những nội dung nào dưới đây:', 'B. Quyết định thành lập Uỷ ban giải phóng ở Hà Nội.'),
(7898, 'Câu 22 Phong trào do Việt Nam Quốc dân Đảng khởi xướng thuộc khuynh hướng nào trong các khuynh hướng sau?', 'A. Phong trào yêu nước theo khuynh hướng dân chủ tư sản.'),
(7899, 'Câu 23 Nét nổi bật nhất của cuộc vận động dân chủ 1936 - 1939 là gì?', 'B. Đảng đã tập hợp được một lực lượng chính trị đông đảo của quân chúng và sử dụng hình thức, phương pháp đấu tranh phong phú.'),
(7900, 'Câu 24 Đại biểu các tổ chức cộng sản nào đã tham dự Hội nghị hợp nhất thành lập Đảng đầu năm 1930?', 'C. Đông Dương Cộng sản Đảng và An Nam Cộng sản Đảng.'),
(7901, 'Câu 25 Tổ chức cộng sản Đông Dương Cộng sản Liên đoàn gia nhập Đảng Cộng sản Việt Nam vào thời gian nào?', 'C. 24/02/1930.'),
(7902, 'Câu 26 Cao trào kháng Nhật cứu nước đã diễn ra ở vùng rừng núi và trung du Bắc kỳ với hình thức nào là chủ yếu?', 'C. Chiến tranh du kích cục bộ.'),
(7903, 'Câu 27 Theo chỉ thị của đồng chí Nguyễn Ai Quốc đội Việt Nam tuyên truyền Giải phóng quân được thành lập khi nào?', 'A. 22/12/1944.'),
(7904, 'Câu 28 Trong các văn kiện sau đây, văn kiện nào của Đảng nhấn mạnh “vấn đề thổ địa là cái cốt của cách mạng tư sản dân quyền”?', 'D. Luận cương chính trị (10/1930).'),
(7905, 'Câu 29 Trong những điểm sau đây nói về chính sách cai trị của thực dân Pháp, điểm nào thuộc về chính sách kinh tế?', 'D. Chính sách khai thác thuộc địa.'),
(7906, 'Câu 30 Sự kiện nào được đánh giá là “như chim én nhỏ báo hiệu mùa xuân”?', 'C. Vụ mưu sát tên toàn quyền Méc Lanh của Phạm Hồng Thái.'),
(7907, 'Câu 31 Tác phẩm ‘Tự chỉ trích’ của đồng chí Nguyễn Văn Cừ được phát hành vào thời gian nào?', 'C. 20/7/1939.'),
(7908, 'Câu 32 Thời gian diễn ra cuộc khai thác thuộc địa lần thứ nhất của thực dân Pháp ở Việt Nam?', 'B. 1897 - 1914.'),
(7909, 'Câu 33 Trong những ý nghĩa lịch sử của sự ra đời Đảng Cộng sản Việt Nam, ý nghĩa nào sau đây đánh dấu bước ngoặt vô cùng quan trọng trong lịch sử cách mạng Việt Nam đầu thế kỷ XX?', 'C. Sự ra đời của Đảng Cộng sản Việt Nam đã giải quyết tình trạng khủng hoảng về đường lối cách mạng, về giai cấp lãnh đạo cách mạng Việt Nam ở đầu thế kỷ XX.'),
(7910, 'Câu 34 Phong trào do Phan Châu Trinh lãnh đạo thuộc khuynh hướng nào trong các khuynh hướng sau?', 'B. Phong trào yêu nước theo khuynh hướng dân chủ tư sản.'),
(7911, 'Câu 35 Điểm giống nhau về ý nghĩa của ba sự kiện: khởi nghĩa Bắc Sơn, khởi nghĩa Nam Kì và binh biến Đô Lương là gì?', 'B. Giáng đòn chí tử vào thực dân Pháp, nghiêm khắc cảnh cáo phát xít Nhật, những phát súng đầu tiên báo hiệu một cao trào cách mạng mới.'),
(7912, 'Câu 36 Trong cuộc khai thác thuộc địa lần thứ nhất của thực dân Pháp, giai cấp nào mới được hình thành?', 'C. Giai cấp công nhân.'),
(7913, 'Câu 37 Hội nghị nào dưới đây đã quyết định phát động toàn dân nổi dậy Tổng khởi nghĩa giành chính quyền?', 'A. Hội nghị toàn quốc của Đảng.'),
(7914, 'Câu 38 Chủ trương điều chỉnh chiến lược cách mạng của Đảng được bắt đầu từ Hội nghị Trung ương lần thứ mấy?', 'B. Hội nghị Trung ương lần thứ 6'),
(7915, 'Câu 39 Ngày nào được Đại hội đại biểu toàn quốc lần thứ III của Đảng quyết định lấy làm ngày kỷ niệm thành lập Đảng?', 'C. 03/02/1930'),
(7916, 'Câu 40 Đại hội Đảng lần thứ nhất diễn ra ở đâu?', 'B. Ma Cao (Trung Quốc).'),
(7917, 'Câu 41 Hội nghị Ban chấp hành Trung ương Đảng Cộng sản Đông Dương lần thứ 8 đã thực hiện khẩu hiệu gì?', 'D. “Tịch thu ruộng đất của đế quốc và Việt gian chia cho dân cày nghèo”.'),
(7918, 'Câu 42  Đoạn văn sau đây được Nguyễn Ái Quốc trình bày lúc nào: “Nếu không giải quyết được vấn đề dân tộc giải phóng, không đòi được độc lập tự do cho toàn thể dân tộc, thì chẳng những toàn thể quốc gia dân tộc còn chịu mãi kiếp ngựa trâu, mà quyền lợi của bộ phận giai cấp đến vạn năm sau cũng không đòi được”?', 'C. Trong Hội nghị Trung ương Đảng lần 8 (tháng 5/1941).'),
(7919, 'Câu 43 Luận cương Chính trị do đồng chí Trần Phú khởi thảo ra đời vào thời gian nào?', 'A. 10/1930'),
(7920, 'Câu 44 Mặt trận dân chủ Đông dương chính thức thành lập vào năm nào?', 'C. 1938'),
(7921, 'Câu 45 Hội Việt Nam cách mạng thanh niên thực hiện chủ trương “vô sản hóa” khi nào?', 'B. Cuối năm 1928 đầu năm 1929.'),
(7922, 'Câu 46 Trong những điểm sau đây nói về chính sách thống trị của thực dân Pháp đã tác động mạnh mẽ tới xã hội Việt Nam, điểm nào nói về phân hoá giai cấp ở nước ta?', 'A. Cho ra đời giai cấp mới là công nhân và tư sản Việt Nam.'),
(7923, 'Câu 47 Cho biết đối tượng của cách mạng trong giai đoạn 1936 - 1939', 'C. Một bộ phận đế quốc xâm lược và tay sai'),
(7924, 'Câu 48 Ban thường vụ Trung ương Đảng ra chỉ thị thành lập Hội phản đế đồng minh vào lúc nào?', 'B. 18/11/1930.'),
(7925, 'Câu 49  Nội dung nào trong Hội nghị hợp nhất các tổ chức cộng sản đầu năm 1930 của Việt Nam được đánh giá là: “được thực hiện bằng một con đường khác mà quốc tế cộng sản không lường trước được\" (văn kiện Đảng toàn tập, tập 4, trang 384).', 'B. Thành lập Đảng Cộng sản để lãnh đạo cách mạng Việt Nam.'),
(7926, 'Câu 50 Trong những hoạt động sau đây của Nguyễn Ái Quốc, hoạt động nào chuẩn bị trực tiếp điều kiện về chính trị và tư tưởng cho việc thành lập Đảng Cộng sản Việt Nam?', 'A. Tập bài giảng “Đường Kách mệnh” của Nguyễn Ái Quốc ở các lớp huấn luyện chính trị đã đề cập đến những vấn đề cơ bản của một Cương lĩnh chính trị cho Đang Cộng sản Việt Nam hiện nay.'),
(7927, 'Câu 51 Cụm từ sau để nói về địa danh nào trong kháng chiến chống Pháp: “pháo đài khổng lồ không thể công phá”; “một cỗ máy để nghiền Việt Minh”.', 'B. Điện Biên Phủ.'),
(7928, 'Câu 52 Nhận xét nào sau đây là đúng về các mặt trận dân tộc thống nhất ở Việt Nam trong những năm 1930 - 1945?', 'A. Nền tảng của mặt trận là công nhân và nông dân.'),
(7929, 'Câu 53 Thực dân Pháp xâm lược Việt Nam vào thời gian nào?', 'C. 1858.'),
(7930, 'Câu 54 Đại hội Đảng Cộng sản Đông Dương lần thứ nhất bầu đồng chí nào làm Tổng bí thư?', 'C. Lê Hồng Phong.'),
(7931, 'Câu 55 Hội nghị Ban Chấp hành Trung ương Đảng 5/1941 họp ở đâu? Do ai chủ trì?', 'A. Cao Bằng - Nguyễn Ái Quốc.'),
(7932, 'Câu 56 Trong những điểm sau đây nói về “hoàn cảnh quốc tế cuối thế kỷ XIX đầu thế kỷ XX đã tác động đến sự ra đời của Đảng Cộng sản Việt Nam”, điểm tác động nào nói đến hậu quả của chủ nghĩa tư bản?', 'A. Mâu thuẫn giữa dân tộc thuộc địa với chủ nghĩa đế quốc.'),
(7933, 'Câu 57 Trong số các bài học kinh nghiệm rút ra từ Cách mạng Tháng 8/1945, bài học nào có ý nghĩa hàng đầu trong phương pháp cách mạng của cách mạng Việt Nam?', 'A. Lợi dụng mâu thuẫn trong hàng ngũ kẻ thù'),
(7934, 'Câu 58 Được sự chỉ đạo của Quốc tế Cộng sản, ban lãnh đạo của đảng ở nước ngoài do ai đứng đầu?', 'D. Lê Hồng Phong.'),
(7935, 'Câu 59 Phong trào do Phan Bội Châu khởi xướng thuộc khuynh hướng nào trong các khuynh hướng sau?', 'B. Phong trào yêu nước theo khuynh hướng dân chủ tư sản.'),
(7936, 'Câu 60 Hội nghị lần 6 (tháng 11 - 1939) của Ban chấp hành Trung ương Đảng đã xác định nhiệm vụ của cách mạng Đông Dương lúc này là gì?', 'A. Đặt nhiệm vụ giải phóng dân tộc lên hàng đầu và cấp bách.'),
(7937, 'Câu 61 Cao trào kháng Nhật cứu nước đã diễn ra ở vùng rừng núi và trung du Bắc kỳ với hình thức nào là chủ yếu?', 'D. Chiến tranh du kích cục bộ'),
(7938, 'Câu 62 Phong trào Đông Kinh Nghĩa Thục thuộc khuynh hướng nào trong các khuynh hướng sau?', 'B. Phong trào yêu nước theo khuynh hướng dân chủ tư sản.'),
(7939, 'Câu 63 Dưới chế độ thực dân phong kiến, giai cấp nông dân có yêu cầu bức thiết nhất là gì?', 'C. Độc lập dân tộc.'),
(7940, 'Câu 64 Chỉ thị \"Nhật - Pháp bắn nhau và hành động của chúng ta\" phản ánh nội dung của Hội nghị nào sau đây?', 'C. Hội nghị Ban chấp hành Trung ương Đảng, họp tháng 3-1945'),
(7941, 'Câu 65 Cho biết hình thức tổ chức đấu tranh trong giai đoạn 1936-1939', 'B. Tất cả các hình thức trên'),
(7942, 'Câu 66 Trong các nhà yêu nước tiêu biểu cho phong trào yêu nước cuối thế kỷ XIX đầu thế kỷ XX được kể sau đây, ai là người đứng đầu nghĩa quân Yên Thế?', 'C. Đề Thám.'),
(7943, 'Câu 67 Luận cương Chính trị do đồng chí Trần Phú khởi thảo ra đời vào thời gian nào?', 'D. 10/1930.'),
(7944, 'Câu 68 Trong số các bài học kinh nghiệm rút ra từ Cách mạng Tháng Tám bài học nào có ý nghĩa hàng đầu trong đường lối cách mạng của ĐCS Việt Nam?', 'A. Giương cao ngọn cờ độc lập dân tộc, kết hợp đúng đắn hai nhiệm vụ chống đế quốc và chống phong kiến.'),
(7945, 'Câu 69 Trong số các bài học kinh nghiệm rút ra từ Cách mạng Tháng 8/1945, bài học nào xác định nguồn gốc sức mạnh của Cách mạng Tháng 8/1945?', 'D. Toàn dân nổi dậy trên nền tảng khối liên minh công - nông'),
(7946, 'Câu 70 Sự kiện nào được đánh giá là “như chim én nhỏ báo hiệu mùa xuân”?', 'C. Vụ mưu sát tên toàn quyền Méc Lanh của Phạm Hồng Thái.'),
(7947, 'Câu 71 Chỉ thị \"Nhật - Pháp bắn nhau và hành động của chúng ta\" ra đời khi nào?', 'B. 12/3/1945'),
(7948, 'Câu 72 Hội nghị toàn quốc của Đảng Cộng sản Đông Dương họp ở Tân Trào trong thời gian nào?', 'C. 14-15/8/1945.'),
(7949, 'Câu 73 Ba tư tưởng sau đây được trình bày trong tác phẩm nào của Nguyễn Ái Quốc? 1. Cách mạng là sự nghiệp của quân chúng. 2.Cách mạng phải do đảng theo chủ nghĩa Mác - Lênin lãnh đạo. 3.Cách mạng Việt Nam phải gắn bó và đoàn kết với cách mạng thế giới.', 'A. “Đường kách mệnh”.'),
(7950, 'Câu 74 Chủ trương của Đảng Cộng sản Đông Dương về “cuộc cách mạng giải phóng dân tộc không nhất định phải kết hợp chặt chẽ với cách mạng điền địa” trong giai đoạn 1936 - 1939 thực chất là:', 'B. Sự trở về với tư tưởng cốt lõi của Cương lĩnh chính trị đầu năm 1930.'),
(7951, 'Câu 75 Nội dung nào sau đây nằm trong Cương lĩnh chính trị đầu tiên của Đảng?', 'D. Đánh đổ đế quốc chủ nghĩa Pháp và bọn phong kiến, làm cho nước Nam hoàn toàn độc lập'),
(7952, 'Câu 76 Hội nghị nào của Ban chấp hành Trung ương Đảng nhấn mạnh giải phóng dân tộc là nhiệm vụ bức thiết nhất?', 'A. Hội nghị họp tháng 5/1941.'),
(7953, 'Câu 77 Hội nghị thành lập Đảng đầu năm 1930 có tất cả bao nhiêu đại biểu tham dự?', 'B. 7'),
(7954, 'Câu 78 Sự kiện đánh dấu bước ngoặt trong cuộc đời hoạt động của Nguyễn Ái Quốc là:', 'B. Bỏ phiếu tán thành việc gia nhập Quốc tế III và tham gia sáng lập Đảng Cộng sản Pháp (tháng 12/1920).'),
(7955, 'Câu 79 Tờ báo nào là cơ quan ngôn luận của Hội Việt Nam Cách mạng Thanh niên?', 'A. Báo Thanh Niên.'),
(7956, 'Câu 80 Trong những điểm sau đây nói về chính sách thống trị của thực dân Pháp đã tác động mạnh mẽ tới xã hội Việt Nam điểm nào SAI?', 'B. Chống đế quốc, giải phóng dân tộc là nhiệm vụ hàng đầu.'),
(7957, 'Câu 81 Trong những ý nghĩa lịch sử của sự ra đời Đảng Cộng sản Việt Nam, ý nghĩa nào sau đây thể hiện Nguyễn Ái Quốc đã vận dụng và phát triển sáng tạo học thuyết Mác Lênin về Đảng Cộng sản?', 'D. Đảng Cộng sản Việt Nam thành lập vào đầu năm 1930 là sản phẩm của sự kết hợp chủ nghĩa Mác Lênin với phong trào công nhân và phong trào yêu nước của nhân dân Việt Nam.'),
(7958, 'Câu 1 Bộ Chính trị Trung ương Đảng đề ra kế hoạch giải phóng hoàn toàn miền Nam trong điều kiện lịch sử:', 'B. Tình hình so sánh lực lượng ở miền Nam thay đổi mau lẹ có lợi cho cách mạng.'),
(7959, 'Câu 2 Khẩu hiệu mà ta nêu ra trong Chiến dịch Điện Biên Phủ là:', 'A. “Tất cả cho tiền tuyến, tất cả để chiến thắng!”.'),
(7960, 'Câu 3 Chủ tịch Hồ Chí Minh ra “Lời kêu gọi toàn quốc kháng chiến” vào thời điểm nào?', 'C. Đêm ngày 19/12/1946'),
(7961, 'Câu 4  “…Chúng ta muốn hòa bình, chúng ta phải nhân nhượng. Nhưng chúng ta càng nhân nhượng, thực dân Pháp càng lấn tới, vì chúng quyết tâm cướp nước ta lần nữa…”. Đoạn trích trong “Lời kêu gọi toàn quốc kháng chiến” của Chủ tịch Hồ Chí Minh đã phản ánh:', 'C. Quyết tâm xâm lược của thực dân Pháp.'),
(7962, 'Câu 5 Nguyên nhân chủ quan cơ bản nhất dẫn đến thắng lợi của cuộc kháng chiến chống Mĩ cứu nước là:', 'B. Sự lãnh đạo sáng suốt của Đảng đứng đầu là chủ tịch Hồ Chí Minh'),
(7963, 'Câu 6 Đại hội Đại biểu lần thứ II của Đảng (2/1951) đã thông qua bản Cương lĩnh nào?', 'B. Chính cương của Đảng Lao động Việt Nam'),
(7964, 'Câu 7 Tổ chức chính trị được ra đời từ phong trào Đồng Khởi là:', 'A. Mặt trận dân tộc giải phóng miền nam Việt Nam'),
(7965, 'Câu 8 Nguyên nhân nào sau đây không phải là nguyên nhân quan trọng đưa tới thắng lợi của cuộc kháng chiến chống Mỹ, cứu nước?', 'A. Có bối cảnh quốc tế thuận lợi, kẻ địch bị khủng hoảng về kinh tế'),
(7966, 'Câu 9 Quyền dân tộc cơ bản mà nhân dân Việt Nam được ghi nhận trong nội dung Hiệp định Giơ-ne-vơ năm 1954 về Đông Dương là:', 'B. Độc lập, chủ quyền, thống nhất và toàn vẹn lãnh thổ.'),
(7967, 'Câu 10 Tháng 9/1953, Bộ Chính trị Trung ương Đảng đề ra chủ trương tập trung lực lượng mở những cuộc tiến công vào những hướng quan trọng về chiến lược mà địch tương đối yếu nhằm:', 'B. Làm thất bại kế hoạch tập trung binh lực của thực dân Pháp.'),
(7968, 'Câu 11 Đông Khê được chọn là nơi mở đầu chiến dịch Biên giới thu - đông năm 1950 của quân dân Việt Nam, vì đó là vị trí:', 'A. Có thể đột phá, chia cắt tuyến phòng thủ của quân Pháp.'),
(7969, 'Câu 12 Đảng và Chính phủ quyết định kêu gọi toàn dân kháng chiến chống thực dân Pháp vào 12/1946 vì:', 'D. không thể tiếp tục nhân nhượng trước những hành động xâm lược của Pháp.'),
(7970, 'Câu 13  Đọc đoạn tư liệu và trả lời câu hỏi: “Muốn củng cố nền tự do, độc lập, chúng ta cần sức hy sinh phấn đấu của toàn quốc đồng bào; nhưng chúng ta cũng rất cần sự quyên giúp của nhân dân, nhất là những nhà giầu có. Ý nghĩa của Tuần lễ vàng là ở đó” (Hồ Chí Minh toàn tập, tập 4, trang 16). Việc tổ chức thành công Tuần lễ vàng năm 1945 ở Việt Nam đã chứng tỏ điều gì?', 'B. Sự sáng tạo của Đảng và Chính phủ trong việc huy động quần chung tham gia việc nước.'),
(7971, 'Câu 14 Trước cuộc tấn công mùa đông của giặc Pháp 1947, Đảng ta quyết tâm bảo vệ căn cứ địa Việt Bắc vì:', 'C. Việt Bắc tập trung các cơ quan đầu não của ta.'),
(7972, 'Câu 15 Trên cơ sở phân tích tình hình Điện Biên Phủ, Trung ương Đảng quyết định mở Chiến dịch Điện Biên Phủ, biến Điện Biên Phủ thành điểm:', 'A. Quyết chiến chiến lược giữa ta và địch.'),
(7973, 'Câu 16 Sự kiện trực tiếp nào đưa đến quyết định toàn quốc kháng chiến của Đảng và Chính phủ?', 'D. Ngày 18/12 thực dân Pháp đưa liên tiếp ba tối hậu thư cho chính phủ ta.'),
(7974, 'Câu 17 Để khắc phục tình trạng khó khăn về tài chính sau Cách mạng tháng Tám, Đảng, Chính phủ đã có chủ trương trước mắt nào sau đây?', 'B. “Quỹ độc lập”.'),
(7975, 'Câu 18 Đảng chủ trương trường kì kháng chiến vì:', 'D. Tương quan lực lượng ban đầu không có lợi cho ta.'),
(7976, 'Câu 19 Phương châm chiến lược trong hai cuộc kháng chiến chống Pháp và chống Mĩ xâm lược (1945-1975) của nhân dân Việt Nam có điểm chung nào?', 'B. Giữ vững tư tưởng tích cực tiến công.'),
(7977, 'Câu 20 Sự kiện nào sau đây khiến Đảng ta thay đổi chiến lược từ hoà hoãn với Tưởng để chống Pháp sang hòa hoãn với Pháp để đuổi Tưởng?', 'D. Hiệp ước Hoa - Pháp được kí kết (28-02-1946).'),
(7978, 'Câu 21 Nhận xét nào sau đây là đúng về bản Hiệp định Sơ bộ (6/3/1946)?', 'C. Biến hiệp ước 2 bên thành thỏa thuận 3 bên có lợi cho cách mạng.'),
(7979, 'Câu 22 Bài học cơ bản cho cách mạng Việt Nam được rút ra từ việc tổ chức thành công Đại hội đại biểu toàn quốc lần thứ III của Đảng Lao động Việt Nam (9/1960) là gì?', 'D. Mềm dẻo linh hoạt trong thực hiện lãnh đạo, chỉ đạo cách mạng.'),
(7980, 'Câu 23 Đặc điểm lớn nhất, độc đáo nhất của cách mạng Việt Nam thời kì 1954-1975 là:', 'D. Một Đảng lãnh đạo nhân dân tiến hành đồng thời hai chiến lược cách mạng ở hai miền Nam- Bắc.'),
(7981, 'Câu 24 Nguyên tắc quan trọng nhất của Đảng, Chính phủ và nhân dân Việt Nam trong việc kí Hiệp định Sơ bộ (06/3/1946) và Hiệp định Giơnevơ (21/7/1954) là:', 'D. Không vi phạm chủ quyền dân tộc.'),
(7982, 'Câu 25 Hội nghị lần thứ 15 Ban chấp hành Trung ương Đảng (01/1959) đã để lại bài học kinh nghiệm nào cho Đảng trong quá trình lãnh đạo cách mạng Việt Nam?', 'A. Sử dụng bạo lực cách mạng giành chính quyền về tay nhân dân.'),
(7983, 'Câu 26 Đường lối chiến lược chung của cách mạng Việt Nam được thông qua tại Đại hội III của Đảng (9/1960) xác định vai trò của cách mạng miền Bắc đối với cách mạng cả nước là?', 'C. Vai trò quyết định nhất'),
(7984, 'Câu 27 Nhiệm vụ của Cách mạng miền Nam Việt Nam sau 1954 là:', 'B. Tiếp tục cuộc cách mạng dân tộc dân chủ nhân dân, thực hiện thống nhất nước nhà'),
(7985, 'Câu 28 Tổ chức chính trị được ra đời từ trong phong trào Đồng Khởi là:', 'A. Mặt trận dân tộc giải phóng Miền nam Việt Nam.'),
(7986, 'Câu 29 Vì sao Bộ Chính trị Trung ương Đảng lựa chọn Tây Nguyên là hướng tiến công chiến lược đầu tiên trong cuộc Tổng tiến công và nổi dậy Xuân 1975?', 'C. Tây Nguyên là địa bàn chiến lược quan trọng, nhưng lực lượng địch mỏng bố trí sơ hở.'),
(7987, 'Câu 30 Hội nghị Bộ chính trị Trung ương Đảng (9-1953) đề ra kế hoạch Đông - Xuân (1953-1954) với quyết tâm giữ vững quyền chủ động đánh địch trên cả hai mặt trận nào?', 'A. Chính diện và sau lưng địch.'),
(7988, 'Câu 31  Đọc đoạn trích và trả lời câu hỏi: Ngày 01/4/1975, Bộ Chính trị nhận định: Cách mạng nước ta đang phát triển với nhịp độ “một ngày bằng hai mươi năm”. Do vậy, Bộ Chính trị quyết định: \"chúng ta phải nắm vững thời cơ chiến lược, quyết tâm thực hiện tổng tiến công và nổi dậy, kết thúc thắng lợi chiến tranh giải phóng trong thời gian ngắn nhất. Tốt hơn cả là bắt đầu và kết thúc trong tháng 4 năm nay, không để chậm” (Đảng Cộng sản Việt Nam, Văn kiện Đảng Toàn tập, Tập 36, Nxb Chính trị Quốc gia, Hà Nội, 2004, tr.95). Nội dung đoạn trích trên phản ánh chủ trương nào của Bộ Chính trị Trung ương Đảng Cộng sản Việt Nam?', 'A. Sự điều chỉnh thời gian giải phóng hoàn toàn miền Nam.'),
(7989, 'Câu 32 Trong các nguyên nhân thắng lợi của cuộc kháng chiến chống thực dân Pháp xâm lược, nguyên nhân nào là căn bản nhất?', 'A. Có sự lãnh đạo đúng đắn của Đảng'),
(7990, 'Câu 33 Biện pháp của Đảng để đối phó với quân Trung Hoa Dân Quốc và bọn phản cách mạng ở miền Bắc là nhân nhượng cho chúng một số yêu sách về:', 'A. Kinh tế, chính trị.'),
(7991, 'Câu 34 Từ sau ngày 2/9/1945 đến trước ngày 6/3/1946, Đảng, Chính phủ, chủ tịch Hồ Chí Minh đã chủ trương tạm thời hòa hoãn với:', 'D. Trung Hoa Dân quốc'),
(7992, 'Câu 35 Sự kiện nào dưới đây chứng tỏ nhân dân Việt Nam đã được thực hiện quyền công dân sau Cách mạng tháng Tám?', 'A. Tổng tuyển cử bầu Quốc hội trong cả nước (1/1946).'),
(7993, 'Câu 36 Để bồi dưỡng sức dân, đặc biệt là nông dân, năm 1953, Đảng và Chính phủ đã:', 'A. Phát động triệt để giảm tô và cải cách ruộng đất.'),
(7994, 'Câu 37 Thành công của cuộc Tổng tuyển cử bầu Quốc hội (1946) ở Việt Nam chứng tỏ:', 'D. Nhân dân có tinh thần đoàn kết và ý thức làm chủ đất nước.'),
(7995, 'Câu 38 Quân và dân ta thực hiện được khẩu hiệu “đánh cho Mĩ cút” thông qua thắng lợi nào dưới đây?', 'C. Hiệp định Pari về Việt Nam năm 1973.'),
(7996, 'Câu 39 Sách lược đấu tranh chống ngoại xâm của ta trong năm đầu sau Cách mạng tháng Tám chứng tỏ:', 'D. Đường lối sáng suốt, cứng rắn về nguyên tắc, mềm dẻo về sách lược của Đảng.'),
(7997, 'Câu 40 Đảng ta đã phát động cuộc kháng chiến toàn quốc chống thực dân Pháp xuất phát từ lí do chủ yếu nào?', 'A. Nền độc lập chủ quyền nước ta bị đe dọa nghiêm trọng.'),
(7998, 'Câu 41 Đại hội đại biểu toàn quốc lần thứ III (9/1960) xác định “tiền tuyến lớn” trong cuộc kháng chiến chống Mĩ xâm lược là:', 'B. Miền Nam'),
(7999, 'Câu 42 Vị trí của cách mạng miền Bắc được xác định trong Đại hội đại biểu toàn quốc lần thứ III (9/1960) là:', 'B. Là hậu phương có vai trò quyết định nhất đối với sự nghiệp cách mạng cả nước.'),
(8000, 'Câu 43 Hội nghị lần thứ 15 Ban Chấp hành Trung ương Đảng Lao động Việt Nam (1- 1959) quyết định để nhân dân miền Nam sử dụng bạo lực cách mạng là vì:', 'A. Ta không thể tiếp tục sử dụng biện pháp hoà bình được nữa.'),
(8001, 'Câu 44 Ngày 8/9/1945, Chủ tịch Hồ Chí Minh kí sắc lệnh gì dưới đây?', 'D. Thành lập Nha Bình dân học vụ.'),
(8002, 'Câu 45 Cuối năm 1974 đầu năm 1975, Bộ Chính trị Trung ương Đảng đề ra chủ trương, kế hoạch giải phóng miền Nam Việt Nam dựa trên cơ sở quan trọng nào?', 'C. Ý chí quyết tâm thống nhất đất nước của nhân dân hai miền.'),
(8003, 'Câu 46 Vận mệnh dân tộc Việt Nam sau khi Cách mạng tháng Tám 1945 thành công được ví như', 'A. Ngàn cân treo sợi tóc'),
(8004, 'Câu 47 Văn kiện nào dưới đây được thông qua tại Đại hội đại biểu toàn quốc lần II (2/1951)?', 'C. Báo cáo chính trị của Chủ tịch Hồ Chí Minh.'),
(8005, 'Câu 48 Nội dung nào KHÔNG phản ánh rõ đường lối chiến tranh nhân dân của Đảng Lao động Việt Nam được triển khai ở miền Nam Việt Nam trong kháng chiến chống Mĩ?', 'B. Hoàn thành khởi nghĩa từng phần rồi tiến lên chiến tranh cách mạng.'),
(8006, 'Câu 49 Chiến dịch nào dưới đây thể hiện cách đánh diệt viện của ta?', 'C. Chiến dịch Biên giới thu - đông 1950.'),
(8007, 'Câu 50 Tháng 02-1951, tại Vinh Quang (Chiêm Hóa- Tuyên Quang) đã diễn ra sự kiện nào sau đây?', 'B. Đại hội đại biểu toàn quốc lần thứ II của Đảng cộng sản Đông Dương.'),
(8008, 'Câu 51 Hai nhiệm vụ chiến lược của Đảng để giữ vững thành quả cách mạng 1945 là gì?', 'A. Củng cố, bảo vệ chính quyền cách mạng và ra sức xây dựng chế độ mới.'),
(8009, 'Câu 52 Kịp thời chuyển hướng phương châm tác chiến từ “đánh nhanh, thắng nhanh” sang “đánh chắc, tiến chắc”, “đánh chắc thắng” là chủ trương của ta trong chiến dịch:', 'C. Điện Biên Phủ năm 1954.'),
(8010, 'Câu 53 Trong những năm 1945 -1946, chính phủ Việt Nam dân chủ Cộng hòa chủ trương hòa hoãn với quân Trung Hoa Dân quốc và thực dân Pháp dựa trên nguyên tắc nào?', 'A. Giữ vững độc lập dân tộc.'),
(8011, 'Câu 54 Thời cơ chiến lược đã đến, ta có điều kiện hoàn thành sớm quyết tâm giải phóng miền Nam… Đó là Nghị quyết nào của Đảng ta?', 'B. Nghị quyết của Bộ Chính trị ngày 25-3-1975.'),
(8012, 'Câu 55 Trong cuộc kháng chiến chống Mĩ, cứu nước (1954-1975) địa bàn Tây Nguyên là nơi được Đảng Lao động Việt Nam chọn để mở đầu cho sự kiện trọng đại nào?', 'C. Tổng tiến công và nổi dậy Xuân 1975.'),
(8013, 'Câu 56 Bộ Chính trị Trung ương Đảng đã lựa chọn địa bàn nào là hướng tiến công chủ yếu trong năm 1975?', 'B. Tây Nguyên.'),
(8014, 'Câu 57 Đại hội nào dưới đây của Đảng được xem là “Đại hội kháng chiến thắng lợi”?', 'D. Đại hội đại biểu toàn quốc lần thứ II (02-1951).'),
(8015, 'Câu 58 Lời kêu gọi toàn quốc kháng chiến của chủ tịch Hồ Chí Minh (19/12/1946) đã?', 'C. Đáp ứng đòi hỏi khách quan của lịch sử và nguyện vọng của nhân dân.'),
(8016, 'Câu 59 Năm 1951, Đảng ta chủ trương thành lập Liên minh nhân dân Việt - Miên - Lào nhằm:', 'B. Tăng cường tình đoàn kết nhân dân ba nước Đông Dương.'),
(8017, 'Câu 60 Quyết định cơ bản của Hội nghị Ban chấp hành Trung ương lần thứ 15 (1-1959) là gì?', 'A. Sử dụng bạo lực cách mạng để đánh đổ chính quyền Mĩ- Diệm.'),
(8018, 'Câu 61 Công cụ xây dựng và bảo vệ đất nước Việt Nam Dân chủ Cộng hòa sau năm 1945 là:', 'C. Một bộ máy chính quyền nhà nước dần được hoàn thiện.'),
(8019, 'Câu 62 Trong cuộc Tổng tiến công và nổi dậy Xuân 1975, trận đánh \"điểm huyệt\" của quân đội và Nhân dân Việt Nam trên chiến trường Tây Nguyên diễn ra ở', 'C. Buôn Ma Thuột'),
(8020, 'Câu 63 Sách lược đối ngoại của Đảng ta từ tháng 9/1945 đến tháng 2/1946 là:', 'C. Hòa Trung Hoa Dân quốc, đánh Pháp.'),
(8021, 'Câu 64 Chỉ thị “Kháng chiến kiến quốc” (25/11/1945) của Ban chấp hành Trung ương Đảng xác định kẻ thù chính của ta lúc này là?', 'B. Thực dân Pháp'),
(8022, 'Câu 65 Một trong những nhiệm vụ xuyên suốt của cách mạng miền Bắc từ năm 1960 đến năm 1975 là:', 'C. Tiến hành công nghiệp hóa xã hội chủ nghĩa.'),
(8023, 'Câu 66 Phương châm tác chiến của quân dân ta trong chiến dịch Hồ Chí Minh (4/1975) là:', 'B. “thần tốc, táo bạo, bất ngờ, chắc thắng”, “Cơ động, linh hoạt, chắc thắng”'),
(8024, 'Câu 67 Tác dụng của việc nhân nhượng với Trung Hoa Dân quốc ở miền Bắc nước ta?', 'C. Hạn chế đến mức thấp nhất những hoạt động chống phá của Trung Hoa Dân quốc.'),
(8025, 'Câu 68 Văn kiện nào sau đây có ý nghĩa to lớn như lời hịch cứu quốc, như mệnh lệnh chiến đấu của nhân dân ta trong cuộc kháng chiến chống thực dân Pháp?', 'C. “Lời kêu gọi toàn quốc kháng chiến” của Chủ tịch Hồ Chí Minh.'),
(8026, 'Câu 69 Nguyên nhân quan trọng nhất khiến Đảng ta chủ trương khi tạm thời hòa hoãn với Tưởng để chống Pháp, khi hoà hoãn với Pháp để đuổi Tưởng là:', 'C. Chính quyền của ta còn non trẻ, không thể cùng một lúc chống hai kẻ thù.'),
(8027, 'Câu 70 Trong cuộc kháng chiến chống Pháp (1945-1954), Đảng Cộng sản Đông Dương đề ra đường lối kháng chiến tự lực cánh sinh đã chứng tỏ điều gì?', 'A. Độc lập dân tộc phải do chính mình giành và giữ lấy.'),
(8028, 'Câu 71 Chính phủ và Chủ tịch Hồ Chí Minh kêu gọi đồng bào thực hiện “Tuần lễ vàng”, “Quỹ độc lập” nhằm mục đích gì?', 'A. Giải quyết khó khăn về tài chính.'),
(8029, 'Câu 72 Phương hướng cách mạng miền Nam trong chiến đấu trong chiến lược “chiến tranh đặc biệt” được Đảng xác định như thế nào?', 'D. Kết hợp chặt chẽ lực lượng chính trị và lực lượng vũ trang.'),
(8030, 'Câu 73 Chính sách nhân nhượng quân Trung Hoa Dân quốc của Chính phủ Việt Nam Dân chủ Cộng hòa có tác dụng như thế nào đối với công cuộc chống thù trong giặc ngoài sau Cách mạng tháng Tám năm 1945?', 'B. Để ta tập trung đối phó với cuộc xâm lược của Pháp ở Nam Bộ.'),
(8031, 'Câu 74 Ban Chấp hành Trung ương Đảng họp Hội nghị lần thứ 21 (7/1973) đã nêu rõ nhiệm vụ cơ bản của cách mạng miền Nam là:', 'A. Tiếp tục cuộc cách mạng dân tộc dân chủ nhân dân.'),
(8032, 'Câu 75 Nội dung nào sau đây KHÔNG phải là chủ trương của Đảng ta trong chiến dịch Biên giới thu đông năm 1950?', 'C. Phải phá tan cuộc tấn công mùa đông của giặc Pháp.'),
(8033, 'Câu 76 Nội dung của đường lối kháng chiến chống thực dân Pháp của ta KHÔNG được thể hiện trong văn kiện nào sau đây?', 'A. Chỉ thị phải “phá tan cuộc tấn công mùa đông của giặc Pháp”.'),
(8034, 'Câu 1 Nội dung nào là một trong những quyết định của kì họp thứ nhất Quốc hội khóa VI nước Việt Nam thống nhất (24/6 - 3/7/1976)?', 'D. Quốc huy mang dòng chữ Cộng hòa xã hội chủ nghĩa Việt Nam.'),
(8035, 'Câu 2 Những thành tựu Việt Nam đạt được trong việc thực hiện kế hoạch 5 năm 1986-1990 chứng tỏ điều gì?', 'C. Đường lối đổi mới là đúng, bước đi của công cuộc đổi mới cơ bản là phù hợp.'),
(8036, 'Câu 3 Tại Đại hội đại biểu toàn quốc lần thứ VI (12/1986), Đảng Cộng sản đề ra chủ trương đổi mới về kinh tế là:', 'D. phát triển kinh tế hàng hoá nhiều thành phần theo định hướng XHCN.'),
(8037, 'Câu 4 Tinh thần gì được phát huy qua hai cuộc Tổng tuyển cử bầu Quốc hội năm 1946 và 1976?', 'D. Tinh thần đại đoàn kết dân tộc.'),
(8038, 'Câu 5 Đại hội XIII nhận định chung về tình hình đất nước sau 35 năm đổi mới:', 'C. Đất nước ta chưa bao giờ có cơ đồ, tiềm lực, vị thế và uy tín quốc tế như ngày nay.'),
(8039, 'Câu 6 Trong 15 năm (1986-2000) thực hiện đường lối đổi mới, những thành tựu về kinh tế- xã hội mà người Việt Nam đạt được có ý nghĩa như thế nào?', 'D. Củng cố vững chắc độc lập dân tộc và chế độ xã hội chủ nghĩa.'),
(8040, 'Câu 7 Về công tác xây dựng Đảng nhiệm kỳ 2021 – 2026, Nghị quyết Đại hội XIII của Đảng xác định có bao nhiêu nhiệm vụ trọng tâm và giải pháp đột phá?', 'A. 3 nhiệm vụ trọng tâm và 3 giải pháp đột phá.'),
(8041, 'Câu 8 Sau khi cuộc kháng chiến chống Mĩ cứu nước kết thúc, yếu tố nào còn tồn tại trái với nguyện vọng, tình cảm thiêng liêng của nhân dân Việt Nam?', 'D. Mỗi miền đất nước vẫn tồn tại hình thức tổ chức nhà nước khác nhau.'),
(8042, 'Câu 9 Kế hoạch nhà nước 5 năm (1976-1980) nhân dân Việt Nam là: vừa xây dựng chủ nghĩa xã hội vừa:', 'D. Cải tạo quan hệ sản xuất xã hội chủ nghĩa'),
(8043, 'Câu 10 Cuộc tổng tuyển cử bầu Quốc hội chung của nước Việt Nam thống nhất được tiến hành vào ngày nào?', 'C. 25/4/1976'),
(8044, 'Câu 11 Việc hoàn thành thống nhất đất nước về mặt nhà nước ở Việt Nam (1976) đã:', 'B. Tạo khả năng to lớn để mở rộng quan hệ với các nước trên thế giới.'),
(8045, 'Câu 12 Khẩu hiệu “Đoàn kết – Dân chủ - Kỉ cương – Đổi mới” lần đầu tiên xuất hiện tại:', 'C. Đại hội đại biểu toàn quốc lần thứ XII'),
(8046, 'Câu 13 Kế hoạch nhà nước 5 năm (1976-1980) nhân dân Việt Nam là: vừa xây dựng chủ nghĩa xã hội vừa:', 'C. Cải tạo quan hệ sản xuất xã hội chủ nghĩa.'),
(8047, 'Câu 14 Từ sau năm 1975 tới nay, sự kiện nào đã đánh dấu bước ngoặt mới cho sự phát triển của lịch sử dân tộc Việt Nam?', 'C. Đường lối đổi mới của Đảng năm 1986.'),
(8048, 'Câu 15 Đại hội lần thứ V của Đảng khẳng định điều gì?', 'A. Tiếp tục thực hiện đường lối Cách mạng XHCN đã vạch ra đại hội lần thứ IV'),
(8049, 'Câu 16 Khoán 100 đã ra đời sau bước đột phát nào của Đảng về kinh tế?', 'D. Bước đột phá đầu tiên về đổi mới kinh tế của Đảng (8/1979).'),
(8050, 'Câu 17 Đâu là điểm chung trong quyết định của Quốc hội khóa VI và Quốc Hội khóa I?', 'D. Bầu Ban dự thảo Hiến pháp.'),
(8051, 'Câu 18 Nội dung nào là một trong những quyết định của kì họp thứ nhất Quốc hội khóa VI nước Việt Nam thống nhất (24/6 đến 3/7/1976)?', 'A. Quyết định Tên nước là Cộng hòa xã hội chủ nghĩa Việt Nam.'),
(8052, 'Câu 19 Sự kiện nào là quan trọng nhất trong quá trình thống nhất đất nước về mặt nhà nước sau năm 1975?', 'D. Quốc hội khóa VI của nước Việt Nam thống nhất họp phiên đầu tiên.'),
(8053, 'Câu 20 Thành tựu quan trọng nhất trong bước đầu thực hiện công cuộc đổi mới (12-1986) là:', 'B. Từng bước đưa đất nước thoát khỏi khủng hoảng kinh tế - xã hội.'),
(8054, 'Câu 21 Nội dung nào không nằm trong đường lối đổi mới về kinh tế của Đảng Cộng sản Việt Nam từ Đại hôi VI (12/1986).', 'D. Ưu tiên đầu tư phát triển công nghiệp nặng.'),
(8055, 'Câu 22 Tên nước Cộng hòa xã hội chủ nghĩa Việt Nam được thông qua tại sự kiện chính trị nào?', 'A. Kì họp thứ nhất Quốc hội khóa VI nước Việt Nam thống nhất (7-1976).'),
(8056, 'Câu 23 Đại hội nào của Đảng được gọi là Đại hội “trí tuệ - đổi mới, dân chủ - kỉ cương – đoàn kết”?', 'A. Đại hội đại biểu toàn quốc lần thứ VII (6-1991)'),
(8057, 'Câu 24 Nhận định nào đúng về Đại hội đại biểu toàn quốc lần thứ VI của Đảng?', 'B. Đại hội khởi xướng đường lối đổi mới toàn diện, đánh dấu bước phát triển mới trong thời kỳ quá độ lên chủ nghĩa xã hội.'),
(8058, 'Câu 25 So với Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội ở Việt Nam (1991), Cương lĩnh bổ sung phát triển năm 2011 nêu thêm đặc trưng của chủ nghĩa xã hội mà nhân dân Việt Nam xây dựng là:', 'B. dân giàu, nước mạnh, dân chủ, công bằng, văn minh.'),
(8059, 'Câu 26 Thành tựu nổi bật về lương thực-thực phẩm trong kế hoạch Nhà nước 5 năm 1986-1990 ở Việt Nam là gì?', 'A. Đáp ứng nhu cầu trong nước, có dự trữ và xuất khẩu.'),
(8060, 'Câu 27 Đâu là nhận xét đúng về Cương lĩnh 2011 của Đảng?', 'D. Là Cương lĩnh hoàn thiện về lý luận về đường lối đổi mới.'),
(8061, 'Câu 28 Hội nghị Hiệp thương chính trị thống nhất đất nước được tổ chức tại Sài Gòn (11-1975) đã:', 'C. Nhất trí hoàn toàn các chủ trương, biện pháp thống nhất đất nước về mặt nhà nước.'),
(8062, 'Câu 29 Nguyện vọng, tình cảm thiêng liêng của nhân dân hai miền Nam – Bắc Việt Nam sau đại thắng mùa Xuân 1975 là:', 'A. Sớm được sum họp trong một đại gia đình.'),
(8063, 'Câu 30 “Việt Nam là bạn, đối tác tin cậy của các nước trong cộng đồng quốc tế, tham gia tích cực vào tiến trình hợp tác quốc tế và khu vực” là chủ trương của đại hội X trên lĩnh vực nào?', 'C. Đối ngoại.'),
(8064, 'Câu 31 Trong mục tiêu tổng quát, Nghị quyết Đại hội lần thứ XII đã thống nhất cần: Đẩy mạnh toàn diện, đồng bộ công cuộc đổi mới; phát triển kinh tế nhanh, bền vững, phấn đấu sớm đưa nước ta…', 'C. Cơ bản trở thành nước công nghiệp theo hướng hiện đại.'),
(8065, 'Câu 32 Tại Đại hội lần thứ IX (4-2001), Đảng chủ trương thực hiện nhất quán và lâu dài mô hình kinh tế tổng quát của Việt Nam trong thời kì quá độ đi lên CNXH là:', 'C. Nền kinh tế thị trường định hướng xã hội chủ nghĩa.'),
(8066, 'Câu 33 Nhận định nào đúng về ý nghĩa của Đại hội đại biểu toàn quốc lần thứ VI của Đảng cộng sản Việt Nam (12/1986)?', 'A. Chuẩn bị những tiền đề cần thiết để Đảng đề ra đường lối đổi mới.'),
(8067, 'Câu 34 Năm 1986, Đảng và Nhà nước Việt Nam tiến hành đổi mới đất nước nhằm:', 'B. Khắc phục sai lầm, khuyết điểm.'),
(8068, 'Câu 35 Trong đường lối đổi mới đất nước (12-1986), Đảng Cộng sản Việt Nam xác định trọng tâm của công cuộc đổi mới là:', 'B. Kinh tế.'),
(8069, 'Câu 36 Đại hội nào của Đảng được gọi là Đại hội “Trí tuệ- đổi mới, dân chủ - kỉ cương - đoàn kết”.', 'C. Đại hội đại biểu toàn quốc lần thứ VII (6-1991).'),
(8070, 'Câu 37 Đâu là nhận xét đúng về chủ trương mở rộng, đa dạng hoá và đa phương hoá quan hệ đối ngoại của Đảng?', 'C. Là chủ trương đúng đắn, kịp thời của Đảng được đưa ra lần đầu tiên ở đại hội VI (12-1986).'),
(8071, 'Câu 38 Đại hội nào của Đảng khẳng định: Tư tưởng Hồ Chí Minh không chỉ là kết quả của sự vận dụng sáng tạo mà còn phát triển sáng tạo chủ nghĩa Mác - Lênin?', 'A. Đại hội IX.'),
(8072, 'Câu 39 Đại hội Đại biểu toàn quốc lần thứ IV của Đảng (12/1976) đề ra đường lối:', 'A. Xây dựng chủ nghĩa xã hội trong phạm vi cả nước'),
(8073, 'Câu 40 Trong quá trình lãnh đạo đất nước thời kỳ đổi mới (1986-nay), Đảng Cộng sản Việt Nam đã rút ra được bao nhiêu bài học kinh nghiệm?', 'A. 5'),
(8074, 'Câu 41 Tại đại hội lần thứ VI (12/1986) Đảng tổng kết một trong những bài học kinh nghiệm rút ra trong toàn bộ hoạt động của mình là Đảng cần phải quán triệt tư tưởng:', 'B. Lấy dân làm gốc.'),
(8075, 'Câu 42 Cương lĩnh xây dựng đất nước trong thời kỳ quá độ đi lên chủ nghĩa xã hội được thông qua tại Đại hội:', 'D. Đại hội đại biểu toàn quốc lần thứ VII (6-1991)'),
(8076, 'Câu 43 Văn kiện Đại hội XIII định hướng bảo vệ Tổ quốc giai đoạn 2021-2030 như thế nào?', 'C. Kiên quyết, kiên trì bảo vệ vững chắc độc lập, chủ quyền, thống nhất, toàn vẹn lãnh thổ của Tổ quốc.'),
(8077, 'Câu 44 Phương châm chỉ đạo của Đại hội XII của Đảng?', 'D. Đoàn kết - Dân chủ - Kỷ cương - Đổi mới.'),
(8078, 'Câu 45 Nội dung đổi mới về kinh tế ở Việt Nam (12-1986) và Chính sách kinh tế mới (NEP, 1921) ở nước Nga có điểm tương đồng là:', 'D. Xây dựng nền kinh tế nhiều thành phần có sự quản lý của nhà nước.'),
(8079, 'Câu 46 Nguyên nhân khách quan nào sau đây thúc đẩy Đảng và Nhà nước ta phải tiến hành đổi mới?', 'B. Do tác động của cuộc cách mạng khoa học công nghệ.'),
(8080, 'Câu 47 Khẩu hiệu “Đoàn kết - Dân chủ - Kỉ cương - Đổi mới” lần đầu tiên xuất hiện tại:', 'D. Đại hội đại biểu toàn quốc lần thứ XII.'),
(8081, 'Câu 48  Cho các dữ liệu sau: 1. Hội nghị lần thứ 24 của Đảng đề ra nhiệm vụ hoàn thành thống nhất đất nước về mặt nhà nước. 2. Hội nghị Hiệp thương chính trị thống nhất đất nước tổ chức tại Sài Gòn. 3. Quốc hội khóa VI nước Việt Nam thống nhất họp kì đầu tiên tại Hà Nội. Sắp xếp các dữ liệu theo thứ tự thời gian thể hiện quá trình thống nhất đất nước về mặt nhà nước.', 'B. 1,2,3.'),
(8082, 'Câu 49 Vì sao nói đổi mới đất nước là một tất yếu khách quan?', 'B. Đất nước ta lâm vào cuộc khủng hoảng kinh tế xã hội trầm trọng.'),
(8083, 'Câu 50 Sự kiện nào đánh dấu công cuộc thống nhất đất nước về mặt nhà nước đã hoàn thành?', 'C. Kì họp đầu tiên Quốc hội khóa VI (1976).'),
(8084, 'Câu 51 Bài học kinh nghiệm lớn nhất được rút ra từ thắng lợi của cuộc Tổng tuyển cử bầu Quốc hội khóa I và khóa VI ở nước ta là gì?', 'A. Phát huy quyền làm chủ nhân dân, không ngừng chăm lo, bồi dưỡng sức dân.'),
(8085, 'Câu 52 Đại hội nào coi nông nghiệp là mặt trận hàng đầu?', 'B. Đại hội đại biểu toàn quốc lần thứ V (3-1981).'),
(8086, 'Câu 53 Phát biểu nào sau đây không đúng về đường lối đổi mới trên lĩnh vực chính trị:', 'D. Mở rộng quan hệ kinh tế đối ngoại với tất cả các quốc gia.'),
(8087, 'Câu 54 “Đoàn kết – Dân chủ – Kỷ cương – Sáng tạo – Phát triển” là gì của Đại hội XIII của Đảng?', 'B. Phương châm chỉ đạo của Đại hội Đảng XIII.'),
(8088, 'Câu 55 Nội dung của Ba chương trình kinh tế lớn từ năm 1986 là:', 'B. Lương thực - thực phẩm, hàng tiêu dùng và hàng xuất khẩu.'),
(8089, 'Câu 56 Một trong những quan điểm được đề ra trong Chiến lược phát triển kinh tế - xã hội 2011 - 2020?', 'D. Phát triển nhanh gắn liền với phát triển bền vững, phát triển bền vững là yêu cầu xuyên suốt trong Chiến lược.'),
(8090, 'Câu 57 Lần đầu tiên, Đại hội đại biểu toàn quốc lần thứ V (3/1982) của Đảng xác định thời kì quá độ đi lên CNXH ở Việt Nam là khó khăn, phức tạp, lâu dài, phải trải qua:', 'C. Nhiều chặng đường.'),
(8091, 'Câu 58 Đường lối đổi mới được thông qua lần đầu tiên tại:', 'C. Đại hội VI (12-1986).'),
(8092, 'Câu 59 Tình hình thế giới và trong nước những năm 80 của thế kỉ XX đã đặt ra yêu cầu cấp bách nào đối với Việt Nam?', 'A. Tiến hành công cuộc đổi mới toàn diện.'),
(8093, 'Câu 60 Phát biểu ý kiến của anh (chị) về nhận định sau: “thống nhất đất nước vừa là nguyện vọng tha thiết bậc nhất của nhân dân, vừa là quy luật khách quan của lịch sử Việt Nam”?', 'B. Đúng, vì thực tế lịch sử Việt Nam đã chứng minh quy luật thống nhất là đúng và đa số người dân đều ủng hộ thống nhất khi 98,8% cử tri đi bỏ phiếu.'),
(8094, 'Câu 61 “Việt Nam là bạn, đối tác tin cậy của các nước trong cộng đồng quốc tế, tham gia tích cực vào tiến trình hợp tác quốc tế và khu vực” là chủ trương của đại hội X trên lĩnh vực nào?', 'C. Đối ngoại'),
(8095, 'Câu 62 Sau ngày đất nước thống nhất 1975, các kì Đại hội Đảng sẽ được tổ chức ở đâu?', 'B. Hà Nội.'),
(8096, 'Câu 63 Vấn đề hoàn thành thống nhất đất nước về mặt Nhà nước ở Việt Nam sau đại thắng mùa Xuân 1975 trở nên cấp thiết do:', 'C. Yêu cầu của sự nghiệp xây dựng và bảo vệ Tổ quốc.'),
(8097, 'Câu 64 Hội nghị lần thứ 24 Ban chấp hành Trung ương Đảng Lao Động Việt Nam (9/1975) đã đề ra nhiệm vụ gì?', 'A. Thống nhất đất nước về mặt nhà nước.'),
(8098, 'Câu 65 Một trong những khó khăn, yếu kém của công cuộc đổi mới từ năm 1986 đến năm 1990 là:', 'C. Kinh tế mất cân đối, lạm phát ở mức cao.'),
(8099, 'Câu 66 Nội dung nào KHÔNG nói lên ý nghĩa của việc hoàn thành thống nhất về mặt Nhà nước sau đại thắng mùa Xuân 1975?', 'B. Chứng tỏ sự ủng hộ to lớn của quốc tế với công cuộc xây dựng chủ nghĩa xã hội của ta.'),
(8100, 'Câu 31 Từ cuối năm 1950, Đảng quyết định tạm ngừng phát triển Đảng để củng cố tổ chức Đảng, xuất phát từ lý do nào sau đây?', 'C. Vì phát hiện ở nhiều nơi việc phát triển Đảng “quá nóng”, mắc sai lầm về tiêu chuẩn đảng viên.'),
(8101, 'Câu 32 Đảng chủ trương tăng cường công tác xây dựng Đảng với việc mở đợt phát triển đảng viên mới “Lớp tháng Tám”, tại Hội nghị nào?', 'A. Hội nghị cán bộ Trung ương, ngày 6/4/1947 .'),
(8102, 'Câu 33 Tháng 12/1967, Bộ Chính trị họp đã ra một nghị quyết lịch sử, quyết định nội dung quan trọng nào sau đây?', 'C. Chuyển cuộc chiến tranh cách mạng miền Nam sang thời kỳ mới, thời kỳ tiến lên giành thắng lợi quyết định bằng phương pháp tổng công kích-tổng khởi nghĩa vào tất cả các đô thị, dinh lũy của Mỹ-ngụy trên toàn miền Nam?'),
(8103, 'Câu 34 Chính cương Đảng Lao động Việt Nam (2/1951) xác định mâu thuẫn chủ yếu của xã hội Việt Nam là như thế nào?', 'A. Là mâu thuẫn giữa giữa tính chất dân chủ nhân dân và tính chất thuộc địa'),
(8104, 'Câu 35 Chính cương Đảng Lao động Việt Nam (2/1951) xác định đối tượng chính của cách mạng Việt Nam là ai?', 'B. Đế quốc Pháp và bọn can thiệp Mỹ.'),
(8105, 'Câu 36 Hội nghị nào của Ban Chấp hành Trung ương Đảng (khóa II) đã đề ra kế hoạch ba năm phát triển kinh tế, văn hóa và cải tạo xã hội chủ nghĩa đối với kinh tế cá thể và kinh tế tư bản tư doanh (1958-1960) ở miền Bắc?', 'A. Hội nghị lần thứ 14 (Tháng 11/1958).'),
(8106, 'Câu 37 Đến tháng 12/1946, số lượng đảng viên trong toàn Đảng Cộng sản Đông Dương là bao nhiêu?', 'A. Tăng lên tới hơn 20.000 người.'),
(8107, 'Câu 38 Chính cương Đảng Lao động Việt Nam (2/1951) xác định mâu thuẫn chủ yếu của xã hội Việt Nam là như thế nào?', 'C. Là mâu thuẫn giữa giữa tính chất dân chủ nhân dân và tính chất thuộc địa'),
(8108, 'Câu 39 Đảng khẳng định: “Nhiệm vụ cơ bản hiện nay của cách mạng Việt Nam là đánh đuổi bọn đế quốc xâm lược, giành độc lập và thống nhất thực sự cho dân tộc, xóa bỏ những di tích phong kiến và nửa phong kiến, làm cho người cày có ruộng, phát triển chế độ dân chủ nhân dân, gây cơ sở cho chủ nghĩa xã hội...” là nội dung của văn kiện nào sau đây?', 'A. Chính cương Đảng Lao động Việt Nam (2/1951).'),
(8109, 'Câu 40 Chỉ thị “Kháng chiến kiến quốc” ngày 25/11/1945 Đảng đã xác định ai là kẻ thù chính của dân tộc ta để tập trung ngọn lửa đấu tranh vào chúng?', 'C. Thực dân Pháp.'),
(8110, 'Câu 41 Chính cương Đảng Lao động Việt Nam (2/1951) xác định đối tượng phụ của cách mạng Việt Nam là ai?', 'C. Phong kiến phản động.'),
(8111, 'Câu 42 Chủ tịch Hồ Chí Minh độc tuyên ngôn độc lập ngày 2/9/1945 tại Quảng trường Ba Đình, Hà Nội đã?', 'D. Khai sinh ra nước Việt Nam dân chủ cộng hòa'),
(8112, 'Câu 43 Chiến lược “Chiến tranh cục bộ” được đế quốc Mỹ thực hiện ở Việt Nam vào thời gian nào?', 'A. 1965 – 1968.'),
(8113, 'Câu 44 Đảng đề ra đường lối kháng chiến:“Động viên và phát huy cho được mọi tiềm năng, sức mạnh của dân tộc, mọi nguồn lực vật chất, tinh thần trong nhân dân phục vụ kháng chiến thắng lợi” thuộc nội dung nào sau đây?', 'A. Kháng chiến toàn diện.'),
(8114, 'Câu 45 Hội nghị nào của Ban Chấp hành Trung ương Đảng (khóa II) đã thông qua Nghị quyết về vấn đề hợp tác hóa nông nghiệp, xác định hình thức và bước đi của hợp tác xã ở miền Bắc?', 'D. Hội nghị lần thứ 16 (Tháng 4/1959).'),
(8115, 'Câu 46 Hội nghị lần thứ 18 Ban Chấp hành Trung ương Đảng (1/1970) và Hội nghị Bộ Chính trị (6/1970) đã đề ra chủ trương mới nhằm chống lại chiến lược “Việt Nam hóa chiến tranh\", là nội dung nào sau đây?', 'C. Lấy nông thôn làm hướng tiến công chính, tập trung ngăn chặn và đẩy lùi chương trình “bình định” của địch.'),
(8116, 'Câu 47 Hội nghị lần thứ 11 (3/1965) và Hội nghị lần thứ 12 (12/1965) của Ban Chấp hành Trung ương Đảng đã phát động cuộc kháng chiến chống Mỹ, cứu nước trên phạm vi toàn quốc và đề ra mục tiêu chiến lược của cuộc kháng chiến là nội dung nào sau đây?', 'C. Kiên quyết đánh bại cuộc chiến tranh xâm lược của đế quốc Mỹ trong bất kỳ tình huống nào, nhằm bảo vệ miền Bắc, giải phóng miền Nam.'),
(8117, 'Câu 48 Tháng 2/1950, Đảng và Hồ Chí Minh chủ trương tổng động viên, ra sắc lệnh huy động nhân lực, vật lực, tài lực của toàn dân nhằm mục mục đích gì?', 'A. Để phục vụ đẩy mạnh công cuộc kháng chiến tiến lên giành thắng lợi lớn hơn.'),
(8118, 'Câu 49 Chiến lược “Việt Nam hóa chiến tranh” được đế quốc Mỹ thực hiện ở Việt Nam vào thời gian nào?', 'C. 1969 – 1975.'),
(8119, 'Câu 50 Hội nghị Bộ Chính trị tháng 9/1954 đã đề ra nhiệm vụ chủ yếu nào cho miền Bắc sau khi được giải phóng ?', 'A. Hàn gắn vết thương chiến tranh, phục hồi kinh tế quốc dân.'),
(8120, 'Câu 51 Đảng chủ trương: bất kể trong tình huống nào cũng phải nắm vững thời cơ, giữ vững đường lối chiến lược tiến công. Nhiệm vụ giành dân, giành quyền làm chủ, phát triển thực lực của cách mạng là yêu cầu vừa bức thiết vừa cơ bản trong giai đoạn mới, là nội dung của Hội nghị nào?', 'C. Hội nghị Trung ương lần thứ 21 khóa III (7/1973).'),
(8121, 'Câu 52 Chính cương Đảng Lao động Việt Nam (2/1951) xác định tính chất của xã hội Việt Nam là như thế nào?', 'A. Dân chủ nhân dân, một phần thuộc địa và nửa phong kiến.'),
(8122, 'Câu 53 Chỉ thị nào của Ban Chấp hành Trung ương đã định hướng con đường đi lên của cách mạng Việt Nam sau khi giành được chính quyền?', 'C. Chỉ thị Kháng chiến kiến quốc ngày 25/11/1945 của BCH Trung ương Đảng.'),
(8123, 'Câu 54 Khó khăn của tình hình thế giới đã tác động đến Việt Nam sau Cách mạng Tháng Tám năm 1945 là nội dung nào sau đây?', 'B. Không có nước nào ủng hộ lập trường độc lập và công nhận địa vị pháp lý của Nhà nước Việt Nam Dân chủ Cộng hòa'),
(8124, 'Câu 55 Đảng đề ra đường lối kháng chiến là:“đánh đổ thực dân Pháp xâm lược, giành nền độc lập, tự do, thống nhất hoàn toàn; vì nền tự do dân chủ và góp phần bảo vệ hòa bình thế giới”, thuộc nội dung nào sau đây?', 'C. Mục tiêu kháng chiến.'),
(8125, 'Câu 56 Hội nghị nào của Ban Chấp hành Trung ương Đảng (khóa II) đã chỉ rõ: “Hiện nay đế quốc Mỹ là kẻ thù chính của nhân dân thế giới, và nó đang trở thành kẻ thù chính và trực tiếp của nhân dân Đông Dương, cho nên mọi việc của ta đều nhằm chống đế quốc Mỹ”?', 'D. Hội nghị lần thứ 6 (Tháng 7/1954).'),
(8126, 'Câu 57 Hội nghị nào của Ban Chấp hành Trung ương Đảng (khóa II), đã nghiêm khắc kiểm điểm những sai lầm, trong cải cách ruộng đất và chỉnh đốn tổ chức, công khai tự phê bình trước nhân dân, thi hành kỷ luật đối với một số Ủy viên Bộ Chính trị và Ủy viên Trung ương Đảng?', 'C. Hội nghị lần thứ 10 (Tháng 9/1956).'),
(8127, 'Câu 58 Chủ tịch Hồ Chí Minh ký ban hành Sắc lệnh Luật cải cách ruộng đất trong cuộc kháng chiến chống thực dân Pháp vào thời gian nào?', 'B. Ngày 19/12/1953.'),
(8128, 'Câu 59 Tháng 6/1950, Ban Thường vụ Trung ương Đảng quyết định mở một chiến dịch quân sự lớn tiến công địch dọc tuyến biên giới Việt-Trung thuộc 2 tỉnh Cao Bằng và Lạng Sơn (Chiến dịch Biên giới Thu Đông 1950) nhằm làm thất bại kế hoạch nào của Thực dân Pháp?', 'A. Kế hoạch Rơve.'),
(8129, 'Câu 60 Thuận lợi của tình hình trong nước sau cuộc Cách mạng Tháng Tám năm 1945 là nội dung nào sau đây?', 'D. Hình thành hệ thống chính quyền cách mạng với bộ máy thống nhất từ cấp Trung ương đến cơ sở, ra sức phục vụ lợi ích của Tổ quốc, nhân dân.'),
(8130, 'Câu 1 Nguyễn Ái Quốc từ Liên Xô về Quảng Châu (Trung Quốc) vào thời gian nào?', 'C. Tháng 12 năm 1924'),
(8131, 'Câu 2 Tổ chức Đông Dương Cộng sản liên Đoàn được thành lập vào thời gian nào?', 'D. Tháng 1 - 1930'),
(8132, 'Câu 3 Chi bộ cộng sản đầu tiên gồm mấy đảng viên? Ai làm bí thư chi bộ?error...', 'B. 7 đảng viên - Bí thư Trần Văn Cung'),
(8133, 'Câu 4 Dưới chế độ thực dân phong kiến, giai cấp nông dân Việt Nam có yêu cầu bức thiết nhất là gì?', 'B. Độc lập dân tộc'),
(8134, 'Câu 5 Những giai cấp bị trị ở Việt Nam dưới chế độ thuộc địa của đế quốc Pháp là:', 'A. Công nhân, nông dân, tiểu tư sản, tư sản dân tộc, địa chủ vừa và nhỏ'),
(8135, 'Câu 6 Tổ chức cộng sản nào ra đời đầu tiên ở Việt Nam?', 'C. Đông Dương cộng sản Đảng'),
(8136, 'Câu 7 Thời gian thực dân Pháp tiến hành khai thác thuộc địa lần thứ nhất ở Việt Nam khi nào?', 'C. 1896 - 1913'),
(8137, 'Câu 8 Chi bộ cộng sản đầu tiên ở Việt Nam được thành lập khi nào?', 'A. Cuối tháng 3 năm 1929'),
(8138, 'Câu 9 Tổ chức Đông Dương Cộng sản Đảng được thành lập vào thời gian nào?', 'D. Tháng 6 - 1929'),
(8139, 'Câu 10 Việt Nam Quốc dân Đảng được thành lập vào thời gian nào?', 'D. Tháng 12 năm 1927'),
(8140, 'Câu 11 Đông Dương cộng sản liên đoàn hợp nhất vào Đảng Cộng sản Việt Nam khi nào?', 'C. Ngày 24 - 2 - 1930');
INSERT INTO `questions` (`id`, `question_name`, `correct_answer`) VALUES
(8141, 'Câu 12 Khởi nghĩa Yên Bái nổ ra vào thời gian nào?', 'A. Ngày 9 tháng 2 năm 1930'),
(8142, 'Câu 13 Tên chính thức của tổ chức này được đặt tại Đại hội lần thứ nhất ở Quảng Châu (tháng 5 - 1929) là gì?', 'D. Hội Việt Nam Cách mạng Thanh niên'),
(8143, 'Câu 14 Báo Đời sống công nhân là của tổ chức nào?', 'C. Tổng Liên đoàn Lao động Pháp'),
(8144, 'Câu 15 Sự kiện nào được Nguyễn Ái Quốc đánh giá “như chim én nhỏ báo hiệu mùa Xuân”?', 'A. Vụ mưu sát tên toàn quyền Méclanh của Phạm Hồng Thái'),
(8145, 'Câu 16 Hội Việt Nam Cách mạng Thanh niên thực hiện chủ trương “vô sản hoá” khi nào?', 'C. Cuối năm 1928 đầu năm 1929'),
(8146, 'Câu 17 Đặc điểm ra đời của giai cấp công nhân Việt Nam như thế nào?', 'B. Các nội dung đều đúng'),
(8147, 'Câu 18 Thời gian ra bản Tuyên đạt nêu rõ việc thành lập Đông Dương Cộng sản Liên đoàn?', 'B. Tháng 9 - 1929'),
(8148, 'Câu 19 Đông Dương Cộng sản Đảng và An nam Cộng sản Đảng được ra đời từ tổ chức tiền thân nào?', 'B. Hội Việt Nam cách mạng Thanh niên'),
(8149, 'Câu 20 Trước Chiến tranh thế giới thứ nhất, ở Việt Nam có những giai cấp nào?', 'D. Địa chủ phong kiến, nông dân, tư sản, tiểu tư sản và công nhân'),
(8150, 'Câu 21 Ai là người đã tham gia sáng lập Việt Nam Quốc dân Đảng 1927 ?', 'C. Phạm Tuấn Tài'),
(8151, 'Câu 22 Hội Liên hiệp thuộc địa được thành lập vào năm nào?', 'A. 1921'),
(8152, 'Câu 23 Mâu thuẫn cơ bản và chủ yếu ở Việt Nam đầu thế kỷ XX là mâu thuẫn nào?', 'D. Mâu thuẫn giữa dân tộc Việt Nam với đế quốc xâm lược và tay sai của chúng'),
(8153, 'Câu 24 Khi nào phong trào công nhân Việt Nam hoàn toàn trở thành một phong trào tự giác?', 'B. Năm 1925 (cuộc bãi công Ba Son)'),
(8154, 'Câu 25 Nguyễn Ái Quốc đã đọc Sơ thảo lần thứ nhất Luận cương về vấn đề dân tộc và vấn đề thuộc địa khi nào? ở đâu?', 'D. 7 - 1920 – Pháp'),
(8155, 'Câu 26 Tổ chức An Nam Cộng sản Đảng được thành lập vào thời gian nào?', 'C. Tháng 8 - 1929'),
(8156, 'Câu 27 Nguyễn Ái Quốc lựa chọn con đường giải phóng dân tộc theo khuynh hướng chính trị vô sản vào thời gian nào?', 'A. 1920'),
(8157, 'Câu 28 Ban Thường vụ Trung ương Đảng ra Quyết nghị chấp nhận Đông Dương cộng sản liên đoàn là một bộ phận của Đảng Cộng sản Việt Nam vào thời gian nào?', 'D. Ngày 24 - 2 - 1930'),
(8158, 'Câu 29 Phong trào đòi trả tự do cho cụ Phan Bội Châu diễn ra sôi nổi năm nào?', 'A. 1925'),
(8159, 'Câu 30 Trong đợt khai thác thuộc địa lần thứ nhất của thực dân Pháp ở nước ta có giai cấp mới nào được hình thành?', 'B. Giai cấp công nhân'),
(8160, 'Câu 31 Đại hội lần thứ VII Quốc tế Cộng sản họp ở Matxcơva vào thời gian nào?', 'C. 25 - 7 đến ngày 20 - 8 - 1935'),
(8161, 'Câu 32 Chính quyền Xô viết ở một số vùng nông thôn Nghệ - Tĩnh được thành lập trong khoảng thời gian nào?', 'C. Cuối năm 1930'),
(8162, 'Câu 33 Nguyên nhân chủ yếu và có ý nghĩa quyết định sự bùng nổ và phát triển của cao trào cách mạng Việt Nam năm 1930?', 'C. Sự lãnh đạo của Đảng Cộng sản Việt Nam'),
(8163, 'Câu 34 Trong cao trào dân chủ 1936 - 1939 Đảng chủ trương tập hợp những lực lượng nào nào?', 'A. Mọi lực lượng dân tộc và một bộ phận người Pháp ởĐông Dương.'),
(8164, 'Câu 35 Cao trào cách mạng Việt Nam năm 1930 bắt đầu bị đế quốc Pháp đàn áp khốc liệt từ khi nào?', 'D. Cuối năm 1930'),
(8165, 'Câu 36 Văn kiện nào của Đảng đặt nhiệm vụ chống đế quốc lên hàng đầu?', 'A. Chính cương vắn tắt, Sách lược vắn tắt do Hội nghị thành lập Đảng thông qua'),
(8166, 'Câu 37 Được sự chỉ đạo của Quốc tế Cộng sản, ban lãnh đạo của Đảng ở nước ngoài được thành lập vào năm nào?', 'C. Năm 1934'),
(8167, 'Câu 38 Được sự giúp đỡ của Quốc tế Cộng sản, Đảng ta đã kịp thời công bố chương trình hành động của Đảng Cộng sản Đông Dương vào thời gian nào?', 'A. Tháng 6 năm 1932'),
(8168, 'Câu 39 Do đâu Nguyễn Ái Quốc đã triệu tập và chủ trì Hội nghị thành lập Đảng đầu năm 1930?', 'D. Sự chủ động của Nguyễn Ái Quốc'),
(8169, 'Câu 40 Tên của lực lượng vũ trang được thành lập ở Nghệ Tĩnh trong cao trào cách mạng năm 1930 là gì?', 'D. Tự vệ đỏ'),
(8170, 'Câu 41 Hội nghị thành lập Đảng Cộng sản Việt Nam đã thông qua mấy văn kiện?', 'A. 6 văn kiện'),
(8171, 'Câu 42 Nội dung nào sau đây nằm trong Cương lĩnh đầu tiên của Đảng?', 'B. Tư sản dân quyền cách mạng là thời kỳ dự bị để làm xã hội cách mạng.'),
(8172, 'Câu 43 Lần đầu tiên nhân dân Việt Nam kỷ niệm ngày Quốc tế lao động vào năm nào?', 'C. 1930'),
(8173, 'Câu 44 Phong trào Đông Dương Đại hội sôi nổi nhất năm nào?', 'D. 1936'),
(8174, 'Câu 45 Văn kiện nào của Đảng nhấn mạnh “vấn đề thổ địa là cái cốt của cách mạng tư sản dân quyền”?', 'B. Luận cương chính trị tháng 10 - 1930.'),
(8175, 'Câu 46 Hội nghị Ban chấp hành TƯ tháng 10 năm 1930 đã cử ra bao nhiêu uỷ viên?', 'C. 6 uỷ viên'),
(8176, 'Câu 47 Trong các điểm sau, chỉ rõ điểm khác nhau giữa Cương lĩnh chính trị đầu tiên của Đảng và Luận cương chính trị tháng 10 - 1930 là:', 'B. Chủ trương tập hợp lực lượng cách mạng.'),
(8177, 'Câu 48 Quốc tế Cộng sản họp Đại hội lần thứ 7 ở đâu khi nào?', 'C. Tháng 7 năm 1935, ở Matxcơva'),
(8178, 'Câu 49 Cương lĩnh đầu tiên của Đảng đã xác định mục tiêu chiến lược của cách mạng Việt Nam là gì?', 'C. Làm tư sản dân quyền cách mạng và thổ địa cách mạng để đi tới xã hội cộng sản.'),
(8179, 'Câu 50 Đại biểu các tổ chức cộng sản nào đã tham dự Hội nghị thành lập Đảng đầu năm 1930?', 'A. Đông Dương cộng sản Đảng và An Nam cộng sản Đảng'),
(8180, 'Câu 51 Hội nghị nào của Ban chấp hành Trung ương Đảng chủ trương tạm gác các khẩu hiệu “độc lập dân tộc” và “cách mạng ruộng đất”', 'B. Hội nghị họp tháng 7 - 1936'),
(8181, 'Câu 52 Hội nghị Hợp nhất thành lập Đảng CSVN (3 - 2 - 1930) thông qua các văn kiện nào sau đây:', 'B. Các nội dung đều đúng'),
(8182, 'Câu 53 Vào thời điểm nào Nguyễn Ái Quốc gửi Quốc tế Cộng sản bản Báo cáo về việc thành lập Đảng Cộng sản Việt Nam?', 'A. Ngày 18 - 2 - 1930'),
(8183, 'Câu 54 Luận cương Chính trị do đồng chí Trần Phú khởi thảo ra đời vào thời gian nào?', 'A. Tháng 10 - 1930'),
(8184, 'Câu 55 Ai là Tổng Bí thư đầu tiên của Đảng?', 'D. Trần Phú'),
(8185, 'Câu 56 Hội nghị lần thứ nhất Ban chấp hành Trung ương 10 - 1930 do ai chủ trì?', 'C. Trần Phú'),
(8186, 'Câu 57 Sau Hội nghị thành lập Đảng, Ban chấp hành Trung ương lâm thời của Đảng được thành lập do ai đứng đầu?', 'A. Trịnh Đình Cửu'),
(8187, 'Câu 58 Mục tiêu cụ thể trước mắt của cao trào cách mạng 1936 - 1939 là gì?', 'C. Các quyền dân chủ đơn sơ.'),
(8188, 'Câu 59 Cho biết đối tượng của cách mạng trong giai đoạn 1936 - 1939', 'D. Một bộ phận đế quốc xâm lược và tay sai.'),
(8189, 'Câu 60 Được sự chỉ đạo của Quốc tế Cộng sản, ban lãnh đạo của Đảng ở nước ngoài do ai đứng đầu?', 'D. Lê Hồng Phong'),
